INSERT INTO `aqrarnews` (`id`, `title`, `content`, `file`, `image`, `status`, `created_at`, `updated_at`, `category_id`) VALUES
(1, 'Akvakultura', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Akvakultura məhsulu kimi balıqlar sığortanın predmeti hesab edilirlər.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\">Hansı risklərdən sığortalana bilər?</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">k&uuml;tləvi zəhərlənmə nəticəsində balıqların &ouml;l&uuml;m&uuml;;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">təbii fəlakətlər (zəlzələ, torpaq s&uuml;r&uuml;şməsi, qasırğa, fırtına və dolu) nəticəsində balıqların &ouml;l&uuml;m&uuml;;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">vəhşi heyvanların h&uuml;cumu nəticəsində balıqların &ouml;l&uuml;m&uuml;;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">yanğının birbaşa və ya dolayı təsiri nəticəsində balıqların &ouml;l&uuml;m&uuml;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">&uuml;&ccedil;&uuml;nc&uuml; şəxslərin hərəkətləri nəticəsində balıqların &ouml;l&uuml;m&uuml;.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Əlavə sığorta haqqı &ouml;dəməklə, yuxarıda qeyd olunmuş risklərlə yanaşı, akvakultura məhsulunun oğurlanması, soyğun&ccedil;uluq və quldurluq yolu ilə əldə edilməsi nəticəsində dəymiş zərərlərə də təminat verilə bilər.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsi bağlanması</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">m&uuml;əssisələrdə yetişdirilən n&ouml;vlər &uuml;&ccedil;&uuml;n suyun texniki normativ h&uuml;quqi aktlarda m&uuml;əyyən edilmiş tələblərə uyğun olmalıdır;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">axar su və nasoslarla su təmin edilən m&uuml;əssisələrdə, həmişə işlək vəziyyətdə əlavə nasos və generatorun olmalıdır;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">axar sularla su təmin edilən m&uuml;əssisələrdə suya qarışan tullantıların kənarlaşdırılması &uuml;&ccedil;&uuml;n alət və vasitələrin istifadəsi və g&uuml;ndə ən azı bir dəfə təmizlənməlidir;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">axar sulardan su təmin edən m&uuml;əssisələrdə suyun boşaldılması &uuml;&ccedil;&uuml;n kanalın inşa edilmiş olmalıdır;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">dənizdə yerləşən m&uuml;əssisələrə m&uuml;nasibətdə dəniz nəqliyyatı &uuml;&ccedil;&uuml;n xəbərdarlıq işıqlarının və fosforlu nişanlamaların m&ouml;vcud olmalıdır;</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">vəhşi heyvanların h&uuml;cumuna qarşı lazımi tədbirlər olmalıdır.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı ona təqdim edildiyi sənədlər və məlumatlar əsasında m&uuml;qavilə bağlanması barədə qərarını Fondla razılaşdırmalıdır.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">M&uuml;qavilənin bağlanmasına razılıq verildiyi halda m&uuml;stəqil eskpert tərəfindən sığorta predmetinə baxış ke&ccedil;irilir.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı m&uuml;stəqil ekspert tərəfindən g&ouml;ndərilmiş məlumat və sənədləri təhlil edərək sığorta m&uuml;qaviləsinin bağlanılıb-bağlanılmaması barədə qərar qəbul edir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>&nbsp;</strong>Sığorta haqqının &ouml;dənilməsi</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Sığorta haqqı birdəfəlik və ya hissələrlə &ouml;dənilə bilər.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Sığorta haqqı aşağıdakı hissələrdən ibarətdir:</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">sığortalı tərəfindən &ouml;dənilməli olan hissə;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">d&ouml;vlət b&uuml;dcəsinin vəsaiti hesabına &ouml;dənilən hissə, yəni sığorta haqqının 50%-i.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsində başqa c&uuml;r nəzərdə tutulmamışdırsa sığorta haqqı birdəfəlik &ouml;dənilir.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə-hissə &ouml;dənilməsi nəzərdə tutulduğu halda sığorta haqqının &ouml;dənilməsi qaydası həmin m&uuml;qavilədə m&uuml;əyyən edilir. Hər bir halda sığortalı tərəfindən &ouml;dənilməli olan sığorta haqqının ilk hissəsi həmin məbləğin ən azı 25 faizini təşkil edir. Sığorta haqqının sığortalı tərəfindən &ouml;dənilməli olan hissəsinin tam &ouml;dənilməsi nəzərdə tutulduqda - hamısı, hissə-hissə &ouml;dənilməsi nəzərdə tutulduqda isə sığorta m&uuml;qaviləsində m&uuml;əyyən edilmiş ilk hissəsi aqrar sığorta m&uuml;qaviləsinin bağlandığı g&uuml;n &ouml;dənilir.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə hissə &ouml;dənilməsi nəzərdə tutulduqda sığorta m&uuml;qaviləsi sığorta haqqının ilk hissəsinin &ouml;dənilməsindən sonra və ya sığorta haqqının tam &ouml;dənilməsi nəzərdə tutulduqda onun tam &ouml;dənilməsindən sonra q&uuml;vvəyə minir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta hadisəsi olduqda</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Sığorta hadisələri barədə məlumatı hadisənin baş vermə tarixindən 24 saat ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ıya və ya idarəedici quruma, eyni zamanda qanunvericiliyə uyğun olaraq, həmin hadisə barədə məlumatlandırılmalı olan digər səlahiyyətli d&ouml;vlət orqanlarına məlumat verməlidir.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">Zərərin araşdırılması və qiymətləndirilməsi &uuml;&ccedil;&uuml;n m&uuml;stəqil ekspert cəlb olunur.</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\">M&uuml;stəqil ekspert tərəfindən təqdim olunan rəy və fotoşəkillər əsasında &ouml;dəniş və ya imtina edilir.</span></li>\r\n</ul>', 'laws_1643459732.pdf', 'aqrarnews_1643579703.jpeg', 1, '2022-01-29 08:35:32', '2022-01-31 09:27:40', 2),
(2, 'Bitkiçilik', '<p style=\"text-align:center\"><span style=\"font-size:16px\">Aqrar siğorta predmeti olaraq aşağıdakı bitki&ccedil;ilik məhsulları siğortalanır:</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- taxıllar, paxlalılar və yağlı bitkilərdə &ndash; dən</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- lifli bitkilərdə &ndash; lif;</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- əsas məhsulu k&ouml;k və dibində olan bitkilərdə - k&ouml;k və dib;</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- əsas məhsulu yarpaq olan bitkilərdə - yarpaq;</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- meyvəsi yeyilən bitkilərdə - meyvə;</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\">- yem bitkilərində - b&uuml;t&uuml;n yaşıl hissə.</span></p>\r\n\r\n<h2 style=\"text-align:center\"><span style=\"font-size:16px\"><strong>Hansı bitkilər sığortalana bilər?</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nazirlər Kabinetinin 17 dekabr 2019-cu il tarixli 479 saylı&nbsp;və 22 dekabr 2021-ci il tarixli 398 saylı&nbsp;qərarlarına əsasən, Azərbaycanda&nbsp;aqrar&nbsp;sığorta&nbsp;hazırda&nbsp;41&nbsp;adda bitki&ccedil;ilik məhsullarını əhatə edir. Onlar aşağıdakılardır:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">buğda;</span></li>\r\n	<li><span style=\"font-size:16px\">arpa;</span></li>\r\n	<li><span style=\"font-size:16px\">qarğıdalı;</span></li>\r\n	<li><span style=\"font-size:16px\">kartof</span></li>\r\n	<li><span style=\"font-size:16px\">şəkər &ccedil;uğunduru;</span></li>\r\n	<li><span style=\"font-size:16px\">portağal;</span></li>\r\n	<li><span style=\"font-size:16px\">limon;</span></li>\r\n	<li><span style=\"font-size:16px\">naringi;</span></li>\r\n	<li><span style=\"font-size:16px\">&ccedil;ay;</span></li>\r\n	<li><span style=\"font-size:16px\">&ccedil;əltik;</span></li>\r\n	<li><span style=\"font-size:16px\">t&uuml;t&uuml;n;</span></li>\r\n	<li><span style=\"font-size:16px\">&uuml;z&uuml;m;</span></li>\r\n	<li><span style=\"font-size:16px\">fındıq;</span></li>\r\n	<li><span style=\"font-size:16px\">pambıq;</span></li>\r\n	<li><span style=\"font-size:16px\">g&uuml;nəbaxan;</span></li>\r\n	<li><span style=\"font-size:16px\">soya;</span></li>\r\n	<li><span style=\"font-size:16px\">yonca;</span></li>\r\n	<li><span style=\"font-size:16px\">noxud;</span></li>\r\n	<li><span style=\"font-size:16px\">yaşıl noxud;</span></li>\r\n	<li><span style=\"font-size:16px\">al&ccedil;a;</span></li>\r\n	<li><span style=\"font-size:16px\">albalı;</span></li>\r\n	<li><span style=\"font-size:16px\">alma;</span></li>\r\n	<li><span style=\"font-size:16px\">armud;</span></li>\r\n	<li><span style=\"font-size:16px\">&ccedil;iyələk;</span></li>\r\n	<li><span style=\"font-size:16px\">ərik;</span></li>\r\n	<li><span style=\"font-size:16px\">gavalı;</span></li>\r\n	<li><span style=\"font-size:16px\">gilas;</span></li>\r\n	<li><span style=\"font-size:16px\">heyva;</span></li>\r\n	<li><span style=\"font-size:16px\">xurma;</span></li>\r\n	<li><span style=\"font-size:16px\">nar;</span></li>\r\n	<li><span style=\"font-size:16px\">şaftalı;</span></li>\r\n	<li><span style=\"font-size:16px\">zeytun;</span></li>\r\n	<li><span style=\"font-size:16px\">badam;</span></li>\r\n	<li><span style=\"font-size:16px\">qoz;</span></li>\r\n	<li><span style=\"font-size:16px\">soğan;</span></li>\r\n	<li><span style=\"font-size:16px\">kələm;</span></li>\r\n	<li><span style=\"font-size:16px\">qarpız;</span></li>\r\n	<li><span style=\"font-size:16px\">yemiş (qovun);</span></li>\r\n	<li><span style=\"font-size:16px\">pomidor;</span></li>\r\n	<li><span style=\"font-size:16px\">sarımsaq;</span></li>\r\n	<li><span style=\"font-size:16px\">darı.</span></li>\r\n</ol>\r\n\r\n<h2 style=\"text-align:center\"><span style=\"font-size:16px\">Hansı risklərdən sığortalana bilər?</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">dolu;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">yanğın;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">zəlzələ;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">torpaq s&uuml;r&uuml;şməsi;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">qasırğa;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">fırtına;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">sel və subasma;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">leysan;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">ildırım;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">şaxtavurma (donvurma);</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">normadan artıq yağan qar yağıntıları;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">&uuml;&ccedil;&uuml;nc&uuml; şəxslərin hərəkətləri.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Əlavə sığorta haqqı &ouml;dəməklə portağal, limon, naringi və &uuml;z&uuml;m bitkilərinin məhsullarında dolu riski nəticəsində yaranan miqdar itkisi ilə yanaşı, keyfiyyət itkisinə də təminat verilə bilər.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Əlavə sığorta haqqı &ouml;dəməklə buğda və arpa bitkilərinin dən hissəsi ilə yanaşı, g&ouml;vdə hissəsində də yaranan miqdar itkisinə təminat verilə bilər və bu təminat &uuml;zrə sığorta məbləği bitkinin dən hissəsi &uuml;zrə sığorta məbləğinin 40% &ccedil;ox ola bilməz.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Əlavə sığorta haqqı &ouml;dəməklə, bitki xəstəlikləri və zərərvericilər və x&uuml;susi təhl&uuml;kəli ziyanvericilərin yayılması riskləri nəticəsində yaranan miqdar itkisinə təminat verilə bilər.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:center\"><strong><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsi bağlanması</span></strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığortalı m&uuml;raciətini məhsullara, risklərə və coğrafi ərazilərə g&ouml;rə aqrar sığorta məhsulunun şərtlərində m&uuml;əyyən edilmiş və Fondun rəsmi internet səhifəsində dərc edilmiş tarixlər ərzində etməlidir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">EKTİS-də qeydiyyatdan ke&ccedil;dikdən sonra m&uuml;vafiq sığorta şirkətinə sığortalanmaqla bağlı m&uuml;raciət edilməlidir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsi bağlamaq istəyən şəxsin təqdim etdiyi məlumatların doğruluğu həmin g&uuml;n ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ı tərəfindən EKTİS-də olan məlumatlarla m&uuml;qayisə edilərək yoxlanılır.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı ona təqdim edildiyi sənədlər və məlumatlar əsasında m&uuml;qavilə bağlanması barədə qərarını Fondla razılaşdırmalıdır.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;qavilənin bağlanmasına razılıq verildiyi halda m&uuml;stəqil eskpert tərəfindən sığorta predmetinə baxış ke&ccedil;irilir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı m&uuml;stəqil ekspert tərəfindən g&ouml;ndərilmiş məlumat və sənədləri təhlil edərək sığorta m&uuml;qaviləsinin bağlanılıb-bağlanılmaması barədə qərar qəbul edir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:center\"><strong><span style=\"font-size:16px\">Sığorta haqqının &ouml;dənilməsi</span></strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta haqqı birdəfəlik və ya hissələrlə &ouml;dənilə bilər.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta haqqı aşağıdakı hissələrdən ibarətdir:</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">sığortalı tərəfindən &ouml;dənilməli olan hissə;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">d&ouml;vlət b&uuml;dcəsinin vəsaiti hesabına &ouml;dənilən hissə, yəni sığorta haqqının 50%-i.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsində başqa c&uuml;r nəzərdə tutulmamışdırsa sığorta haqqı birdəfəlik &ouml;dənilir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə-hissə &ouml;dənilməsi nəzərdə tutulduğu halda sığorta haqqının &ouml;dənilməsi qaydası həmin m&uuml;qavilədə m&uuml;əyyən edilir. Hər bir halda sığortalı tərəfindən &ouml;dənilməli olan sığorta haqqının ilk hissəsi həmin məbləğin ən azı 25 faizini təşkil edir. Sığorta haqqının sığortalı tərəfindən &ouml;dənilməli olan hissəsinin tam &ouml;dənilməsi nəzərdə tutulduqda - hamısı, hissə-hissə &ouml;dənilməsi nəzərdə tutulduqda isə sığorta m&uuml;qaviləsində m&uuml;əyyən edilmiş ilk hissəsi aqrar sığorta m&uuml;qaviləsinin bağlandığı g&uuml;n &ouml;dənilir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə hissə &ouml;dənilməsi nəzərdə tutulduqda sığorta m&uuml;qaviləsi sığorta haqqının ilk hissəsinin &ouml;dənilməsindən sonra və ya sığorta haqqının tam &ouml;dənilməsi nəzərdə tutulduqda onun tam &ouml;dənilməsindən sonra q&uuml;vvəyə minir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta hadisəsi olduqda</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta hadisələri barədə məlumatı hadisənin baş vermə tarixindən 24 saat ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ıya və ya idarəedici quruma, eyni zamanda qanunvericiliyə uyğun olaraq, həmin hadisə barədə məlumatlandırılmalı olan digər səlahiyyətli d&ouml;vlət orqanlarına məlumat verməlidir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Zərərin araşdırılması və qiymətləndirilməsi &uuml;&ccedil;&uuml;n m&uuml;stəqil ekspert cəlb olunur.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;stəqil ekspert tərəfindən təqdim olunan rəy və fotoşəkillər əsasında &ouml;dəniş və ya imtina edilir.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 'laws_1643459796.pdf', 'aqrarnews_1643579717.jpg', 1, '2022-01-29 08:36:36', '2022-01-31 09:28:53', 3),
(4, 'Heyvandarlıq', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nazirlər Kabinetinin 17 dekabr 2019-cu il tarixli 479 saylı&nbsp;və 22 dekabr 2021-ci il tarixli 398 saylı&nbsp;qərarlarına əsasən, Azərbaycanda&nbsp;aqrar&nbsp;sığorta&nbsp;hazırda aşağıdakı&nbsp;heyvandarlıq məhsullarını əhatə edir:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- 11-ci g&uuml;ndən 7 yaşa qədər s&uuml;dl&uuml;k mal-qara (inəklər və camışlar);</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- 11-ci g&uuml;ndən 3 yaşa qədər ətlik mal-qara (danalar);</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">- 11-ci g&uuml;ndən 5 yaşa qədər xırdabuynuzlu heyvanlar (qoyun və ke&ccedil;ilər).</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:16px\">Aqrar sığorta heyvanları hansı risklərdən sığortalayır?</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Aşağıdakı risklərin baş verməsi nəticəsində sığortalanmış heyvanın &ouml;l&uuml;m&uuml;:</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">Azərbaycan Respublikası Nazirlər Kabinetinin 2006-cı il 7 mart tarixli 65 n&ouml;mrəli və 2007-ci il 16 aprel tarixli 66 n&ouml;mrəli Qərarlarda təsdiq edilmiş və g&ouml;stərilmiş karantin və x&uuml;susi təhl&uuml;kəli yoluxucu xəstəliklərin siyahısına daxil olan xəstəliklər istisna olmaqla, digər yoluxucu xəstəliklər;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">ilan və ya həşərat sancması;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">zəhərli otlar və ya yemlərdən zəhərlənmə;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">kimyəvi maddələrin təsirindən zəhərlənmə;&nbsp;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">təbii fəlakətlər (zəlzələ, torpaq s&uuml;r&uuml;şməsi, qasırğa, fırtına və dolu);</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">yanğın;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">vəhşi heyvanların h&uuml;cumu;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">&uuml;&ccedil;&uuml;nc&uuml; şəxslərin hərəkətləri;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">əlavə sığorta haqqı &ouml;dəməklə nəzərdə tutulmuş risklərlə yanaşı oğurluq, soyğun&ccedil;uluq və quldurluq yolu ilə əldə edilməsi nəticəsində dəymiş zərərlərə də təminat verilə bilər.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:center\"><strong><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsinin bağlanması</span></strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">heyvan qapalı yerdə saxlanılmalı və saxlandığı yer &ldquo;Heyvanların saxlanması, yetişdirilməsi və istifadə olunması Qaydaları&rdquo;na uyğun olmalıdır ( Nazirlər Kabinetinin 2007-ci il 30 yanvar tarixli 19 n&ouml;mrəli Qərarı);</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">heyvanın &ldquo;Baytarlıq tədbirlərinin təşkili və həyata ke&ccedil;irilməsi Qaydaları&rdquo;na uyğun olaraq qulluq və qidalanması təmin edilməli (Nazirlər Kabinetinin 2007-ci il 23 yanvar tarixli 11 n&ouml;mrəli Qərarı);</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">heyvan peyvəndlənməli və birkalanmış olmalıdır;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">heyvan barədə məlumatlar EKTİS-ə daxil edilnəlidir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">idarəedici qurumun məsul əməkdaşı ona təqdim edildiyi sənədlər və məlumatlar əsasında m&uuml;qavilə bağlanması barədə qərarını Fondla razılaşdırmalıdır.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">m&uuml;qavilənin bağlanmasına razılıq verildiyi halda m&uuml;stəqil eskpert tərəfindən sığorta predmetinə baxış ke&ccedil;irilir.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">idarəedici qurumun məsul əməkdaşı m&uuml;stəqil ekspert tərəfindən g&ouml;ndərilmiş məlumat və sənədləri təhlil edərək sığorta m&uuml;qaviləsinin bağlanılıb-bağlanılmaması barədə qərar qəbul edir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:center\"><strong><span style=\"font-size:16px\">Sığorta haqqının &ouml;dənilməsi</span></strong></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">sığorta haqqı&nbsp;<strong>birdəfəlik və ya hissələrlə</strong>&nbsp;&ouml;dənilə bilər;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">sığorta haqqı aşağıdakı hissələrdən ibarətdir:</span>\r\n	<ul>\r\n		<li><span style=\"font-size:16px\">Sığortalı tərəfindən &ouml;dənilməli olan hissə;</span></li>\r\n		<li><span style=\"font-size:16px\">D&ouml;vlət b&uuml;dcəsinin vəsaiti hesabına &ouml;dənilən hissə, yəni sığorta haqqının 50%-i;</span></li>\r\n	</ul>\r\n	</li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">sığorta m&uuml;qaviləsində başqa hal nəzərdə tutulmamışdırsa, sığorta haqqı birdəfəlik &ouml;dənilir.<br />\r\n	<br />\r\n	Aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə-hissə &ouml;dənilməsi nəzərdə tutulduğu halda sığorta haqqının &ouml;dənilməsi qaydası həmin m&uuml;qavilədə m&uuml;əyyən edilir.&nbsp;<strong>Hər bir halda sığortalı tərəfindən &ouml;dənilməli olan sığorta haqqının ilk hissəsi həmin məbləğin ən azı 25 faizini təşkil edir.</strong>&nbsp;Sığorta haqqının sığortalı tərəfindən &ouml;dənilməli olan hissəsinin tam &ouml;dənilməsi nəzərdə tutulduqda - hamısı, hissə-hissə &ouml;dənilməsi nəzərdə tutulduqda isə sığorta m&uuml;qaviləsində m&uuml;əyyən edilmiş ilk hissəsi aqrar sığorta m&uuml;qaviləsinin bağlandığı g&uuml;n &ouml;dənilir;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">aqrar sığorta m&uuml;qaviləsində sığorta haqqının hissə hissə &ouml;dənilməsi nəzərdə tutulduqda sığorta m&uuml;qaviləsi sığorta haqqının ilk hissəsinin &ouml;dənilməsindən sonra və ya sığorta haqqının tam &ouml;dənilməsi nəzərdə tutulduqda, onun tam &ouml;dənilməsindən sonra q&uuml;vvəyə minir.</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:center\"><span style=\"font-size:16px\">Sığorta hadisəsi olduqda</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">sığorta hadisələri barədə məlumatı hadisənin baş vermə tarixindən 24 saat ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ıya və ya idarəedici quruma, eyni zamanda qanunvericiliyə uyğun olaraq, həmin hadisə barədə məlumatlandırılmalı olan digər səlahiyyətli d&ouml;vlət orqanlarına məlumat verməlidir;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">zərərin araşdırılması və qiymətləndirilməsi &uuml;&ccedil;&uuml;n m&uuml;stəqil ekspert cəlb olunur;</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\">m&uuml;stəqil ekspert tərəfindən təqdim olunan rəy və fotoşəkillər əsasında &ouml;dəniş və ya imtina edilir;</span></li>\r\n</ul>', 'aqrarnews_1643581111.pdf', 'aqrarnews_1643579789.jpg', 1, '2022-01-29 12:47:28', '2022-01-31 09:29:42', 1);

