INSERT INTO `news` (`id`, `title`, `category_id`, `image`, `content`, `status`, `created_at`, `updated_at`) VALUES
(25, 'Payız-qış mövsümünə hazırlıqla bağlı görülən işlər və qarşıda duran vəzifələr müzakirə edilib', 19, 'news_1638439911.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyində ke&ccedil;irilən tədbiri giriş s&ouml;z&uuml; ilə a&ccedil;an kənd təsərr&uuml;fatı naziri Bəhruz Bayramov aidiyyəti &uuml;zrə tapşırıqlar verərək qeyd edib ki, bu g&uuml;n əhalinin maddi rifahının yaxşılaşdırılması &uuml;&ccedil;&uuml;n kənd təsərr&uuml;fatının inkişafı, bazarda yerli məhsulların &uuml;st&uuml;nl&uuml;k təşkil etməsi, kəndlinin &ouml;z torpaqlarından səmərəli istifadəsi ən vacib amillərdən biridir. Bu istiqamətdə muxtar respublikada həyata ke&ccedil;irilən məqsədy&ouml;nl&uuml; tədbirlər kənd təsərr&uuml;fatının dinamik inkişafını təmin etməklə yanaşı, həm də sahibkarlığın artması istiqamətində &ouml;z m&uuml;sbət təsirini g&ouml;stərir. Bununla yanaşı, hazırda muxtar respublikada taxıl&ccedil;ılıq, tərəvəz&ccedil;ilik, meyvə&ccedil;ilik, &uuml;z&uuml;m&ccedil;&uuml;l&uuml;k və digər sahələrin inkişafı stimullaşdırılır, fermerlərə g&uuml;zəştli kreditlərdən və aqrolizinq xidmətlərindən daha geniş istifadə imkanları yaradılır, əkin sahələri genişləndirilir. Eyni zamanda ərzaq təhl&uuml;kəsizliyinin m&ouml;hkəmləndirilməsində x&uuml;susi əhəmiyyətə malik olan ailə-fermer təsərr&uuml;fatlarına yeni innovasiyaların tətbiqi, intensiv metodlara əsaslanan m&uuml;asir təsərr&uuml;fatların yaradılmasına d&ouml;vlət dəstəyi davam etdirilir. Diqqətə &ccedil;atdırılıb ki, muxtar respublikada aparılan meliorasiya, irriqasiya tədbirlərinin, lizinq xidmətinin genişləndirilməsi, taxıl və soyuducu anbarların tutumunun artırılması, istixana komplekslərinin, quş&ccedil;uluq və balıq&ccedil;ılıq təsərr&uuml;fatlarının, meyvətərəvəz emalı m&uuml;əssisələrinin işə salınması kənd təsərr&uuml;fatının s&uuml;rətli inkişafına əlverişlişərait yaratmışdır. Eyni zamanda payızlıq taxıl əkini ilə əlaqədar olaraq şum və səpin aparmaq &uuml;&ccedil;&uuml;n 771 ədəd təkərli, 67 ədəd tırtıllı, 118 ədəd taxılsəpən, 403 ədəd kotan, 78 ədəd &ccedil;izel və şırıma&ccedil;an, 38 ədəd toxumtəmizləyən, 287 ədəd lafet saz vəziyyətə gətirilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vurğulanıb ki, təsərr&uuml;fat işlərinin m&uuml;təşəkkil və optimal surətdə ke&ccedil;irilməsində kənd təsərr&uuml;fatı texnikasının sazlığının başlıca şərtlərdən olmasını nəzərə alaraq nazirliyin texniki nəzarət və aqroservis sektoru tərəfindən texnikaların sazlığı vəziyyəti &ouml;yrənilmiş, lazım olan ehtiyat hissələrinin alınıb gətirilməsi və sahibkarlara &ccedil;atdırılması &uuml;&ccedil;&uuml;n tədbirlər həyata ke&ccedil;irilmişdir. Bəhruz Bayramov 2021- 2022-ci illərin payız-qış m&ouml;vs&uuml;m&uuml; ilə əlaqədar olaraq qarşıda duran vəzifələrə hazırlıq məqsədilə nəzərdə tutulan tədbirlər barədə məlumat verib. Sonra məruzə ətrafında &ccedil;ıxışlar olub.</span></p>\r\n\r\n<p>&nbsp;</p>', 1, '2021-12-02 06:11:51', '2022-01-31 09:12:20'),
(26, 'Kəngərli rayonunda seminar-müşavirə keçirilib', 19, 'news_1638440006.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi Kəngərli Rayon İcra Hakimiyyəti ilə birgə Xok kəndində ailə təsərr&uuml;fat&ccedil;ıları &uuml;&ccedil;&uuml;n&nbsp;&nbsp;maarifləndirici seminar-m&uuml;şavirə ke&ccedil;irib. Tədbirdə əkin atlaslarına uyğun əkinlərin həyata ke&ccedil;irilməsi, yağ &uuml;&ccedil;&uuml;n qarğıdalı və g&uuml;nəbaxan bitkilərinin əkilib-becərilmə texnologiyası, bu kimi bitkilərin əkinin genişləndirilməsi və iqtisadi səmərəsi, həm&ccedil;inin payız şumunun aqrotexniki qaydalar əsasında aparılmasının&nbsp;&nbsp;əhəmiyyətindən danışılıb.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;şavirəni Kəngərli Rayon İcra Hakmiyyətinin n&uuml;mayəndəsi Ağa Seyidov a&ccedil;araq b&ouml;lgədə torpaqların m&uuml;nbitliyinin artırılması,&nbsp;&nbsp;əkinlərin əkin atlaslarına uyğun həyata ke&ccedil;irilməsi, x&uuml;susən də taxıl əkini sahələrinin genişləndirilməsi məsələlərindən danışıb. O torpaq m&uuml;lkiyyət&ccedil;ilərinin&nbsp;&nbsp; iqtisadi səmərə gətirə bilən qarğıdalı və g&uuml;nəbaxan kimi bitkilərin əkilib-becərilməsinə də marağının artdığının bildirərk &nbsp;qeyd edib ki, builki m&ouml;vs&uuml;mdə rayonda 162 hektar sahədə qarğıdalı, 53 hektarda isə g&uuml;nəbaxan əkilib.&nbsp;&nbsp;Hazırda əsas diqqət payızlıq şumun keyfiyyətlə həyata ke&ccedil;irilməsinə və toxum tədar&uuml;k&uuml;nə y&ouml;nəldilib.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin ş&ouml;bə m&uuml;diri Xaliq Abbasov əkin atlaslarından istifadə edərək y&uuml;ksək məhsuldarlığa nail olunmasından, aqrar sahədə qəbul edilən d&ouml;vlət proqramalarının icra vəziyyətindən danışıb, x&uuml;susən də&nbsp;&nbsp;yağ &uuml;&ccedil;&uuml;n qarğıdalı və g&uuml;nəbaxan&nbsp; kimi iqtisadi səmərə gətirən bitkilərin əkilib-becərilmə texnologiyasından danışaraq&nbsp; bildirib ki, torpaqların m&uuml;nbitliyinin artırılması, mineral və &uuml;zv&uuml; g&uuml;brələrdən səmərəli istifadə kənd təsərr&uuml;fatında yağlı bitkilər hesab edilən qarğıdalı və g&uuml;nəbaxan sahələrindən bol məhsul g&ouml;t&uuml;rməyə imkan verir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bu kimi bitkilərdən alınan məhsul həm də sənaye sahəsində&nbsp;&nbsp;keyfiyyəti xammal kimi istifadə olunur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kənd Təsərr&uuml;fatı Nazirliyinin ş&ouml;bə m&uuml;diri Bəhruz Qasımov sahələrdən bol məhsul tədar&uuml;k edilməsində vacib amillərdən olan y&uuml;ksək repreduksiyalı toxumun se&ccedil;ilməsindən və toxumların dərmanlanmasının əhəmiyyətindən, nazirliyin sektor m&uuml;diri Valeh Mehdiyev əkindən əvvəl torpaqların analiz edilərək torpaqda &ccedil;atışmayan mikroelemetləri &uuml;zvi və mineral g&uuml;brələr vasitəsilə torpağa verilərək &nbsp;keyfiyyətli payız şumunun qaldırılmasında m&uuml;asir texnika və avadanlıqların rolunun əhəmiyyətindən bəhs ediblər.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sonda &nbsp;ailə təsərr&uuml;fat&ccedil;ılarını maraqlandıran suallar m&uuml;təxəssislər tərəfindən ətraflı cavablandırılıb. Onlara g&uuml;nəbaxan və qarğıdalı kimi yağlı bitkilərin əkillib-becərilməsi texnologiyası, həm&ccedil;inin iqtisadi səmərəsi, torpağın əsas becərilməsi barədə hazırlanan yaddaş kitab&ccedil;aları paylanılıb.</span></p>\r\n\r\n<p style=\"text-align:right\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong><strong><em>Nax&ccedil;ıvan Muxtar Respublikası</em></strong></strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><em><strong><strong>Kənd&nbsp;</strong><strong>Təsərr&uuml;fatı&nbsp;</strong><strong>Nazirliyinin&nbsp; mətbuat xidməti</strong></strong></em></span></p>\r\n\r\n<p>&nbsp;</p>', 1, '2021-12-02 06:13:26', '2021-12-02 06:13:26'),
(27, 'Topraq mülkiyyətçilərinin iştirakı ilə müşavirə keçirilib', 19, 'news_1638440079.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin əməkdaşları Babək Rayon İcra Hakimiyyəti ilə birlikdə rayonun Cəhri qəsəbəsində Nax&ccedil;ıvan Muxtar Respublikasında aqrar sahənin inkişafı ilə əlaqədar qəbul olunmuş d&ouml;vlət proqramlarının əhəmiyyəti və qarşıya qoyulan vəzifələrin icra edilməsi, payız şumunun qaldırılmasında m&uuml;asir texnika və avadanlıqların rolu, toxumların dərmanlanmasının əhəmiyyəti m&ouml;vzularında torpaq m&uuml;lkiyyət&ccedil;ilərinin iştirakı &nbsp;ilə tarla-seminar m&uuml;şavirəsi ke&ccedil;iriblər.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;şavirəni Babək Rayon İcra Hakimiyyətinin əməkdaşı Azər H&uuml;mbətov a&ccedil;araq bildirib ki, Babək rayonunda kənd təsərr&uuml;fatının inkişaf etdirilməsinə dair qəbul edilən qərarlar, reallaşan d&ouml;vlət proqramları kənd təsərr&uuml;fatı məhsullarının sənaye sahəsinə y&ouml;nəldilməsinə və ixracına şərait yaratmışdır. Bu da əhalinin iqtisadi gəlirlərinin artmasına səbəb olmuşdur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin ş&ouml;bə m&uuml;diri Xaliq Abbasov qeyd edib ki, muxtar respublika şəraitində kənd təsərr&uuml;fatı bitkilərinin əkin atlaslarına uyğun aparılmasının&nbsp; torpaqların m&uuml;nbitliyinin qorunmasında və məhsuldarlığın y&uuml;ksəldilməsində b&ouml;y&uuml;k rolu vardır. Belə ki, əkin atlaslarına uyğun ke&ccedil;irilən əkinlərdə bitkilərin &uuml;zvi və mineral g&uuml;brələrlə təminatı aqrotexniki tələblərə uyğun olur, sahələrdən bol məhsul g&ouml;t&uuml;r&uuml;l&uuml;r.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nazirliyin baş məsləhət&ccedil;isi Səməddin Əsgərov &ccedil;ıxış edərək vurğulayıb ki, əkin sahələrindən y&uuml;ksək məhsul əldə olunmasında pestisidlərin rolu b&ouml;y&uuml;kd&uuml;r. Sahələrdə xəstəlik və zərərvericilərə qarşı m&uuml;barizə tədbirlərinin vaxtında aparılmaması məhsuldarlığın aşağı d&uuml;şməsinə təsir edəcəkdir. Payız şumunun vaxtında və aqrotexniki qaydalara uyğun aparılması torpaqda olan bəzi zərərvericilərin s&uuml;rfələrinin dərin qatlara d&uuml;şərək məhv olmasına şərait yaradır. Xəstəlik və zərərvericilərin qarşısının alınmasında səpindən qabaq toxumların dərmanlanmasının da &nbsp;b&ouml;y&uuml;k əhəmiyyəti vardır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nazirliyin sektor m&uuml;diri Valeh Mehdiyev payız şumunun qaldırılmasında m&uuml;asir kənd təsərr&uuml;fatı texnika və avadanlıqların rolundan danışıb.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sonda torpaq m&uuml;lkiyyət&ccedil;ilərini maraqlandıran suallar cavablandırılıb, m&uuml;təxəssislər tərəfindən hazırlanan maarifləndirici bukletlər m&uuml;şavirə iştirak&ccedil;ılarına paylanıb.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin&nbsp; mətbuat xidməti</strong></span></p>', 1, '2021-12-02 06:14:39', '2021-12-02 06:14:39'),
(28, 'Arıçıların iştirakı ilə seminar-müşavirə keçirilib', 19, 'news_1638440142.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı, İqtisadiyyat, Ekologiya və Təbii Sərvətlər nazirliklərinin, Antiinhisar və İstehlak Bazarına Nəzarət D&ouml;vlət Xidmətinin, D&ouml;vlət Baytarlıq Xidmətinin, &ldquo;Nax&ccedil;ıvan Muxtar Respublikası D&ouml;vlət Sığorta Xidməti&rdquo; publik h&uuml;quqi şəxsinin, AMEA Nax&ccedil;ıvan B&ouml;lməsinin və &ldquo;Nax&ccedil;ıvan Muxtar RespublikasıArı&ccedil;ıları&rdquo; İctimai Birliyinin əməkdaşları Şərur Rayon İcra Hakimiyyəti ilə birgə arı&ccedil;ılıq təsərr&uuml;fatı sahibkarlarının iştirakı ilə &ldquo;Payız m&ouml;vs&uuml;m&uuml;ndə arıxanalarda baytarlıq tədbirlərinin aparılmasının və arıxanaları qışlamaya hazırlamağın əhəmiyyəti&rdquo; m&ouml;vzusunda seminar-m&uuml;şavirə ke&ccedil;iriblər. M&uuml;şavirəni Şərur Rayon İcra Hakimiyyətinin əməkdaşı Yasin Seyidov a&ccedil;araq rayonda arı&ccedil;ılığın inkişafından və g&ouml;stərilən d&ouml;vlət dəstəyindən danışıb. Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin ş&ouml;bə m&uuml;diri Əyyub Abdullayev &ccedil;ıxış edərək vurğulayıb ki, Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin Sərəncamı ilə təsdiq edilmiş &ldquo;2017-2022-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın və Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı ilə bağlı tədbirlər planının icrasına uyğun olaraq bu sahənin inkişafı d&ouml;vlət tərəfindən daim dəstəklənərək sahibkarlara subsidiya, kredit, pulsuz arı dərmanları verilir. Arı ailələrinin k&ouml;&ccedil;&uuml;r&uuml;lməsi &uuml;&ccedil;&uuml;n ərazilər ayrılır, bal s&uuml;zən dəzgahların və arı pətəklərinin lizinq qaydasında sahibkarlara satılması, arı&ccedil;ılıq məhsullarının satışı &uuml;&ccedil;&uuml;n təşkil olunan &ldquo;Arı&ccedil;ılıq məhsulları &ndash; bal festival&rdquo;ları və yarmarkalar arı&ccedil;ılıqla məşğul olan sahibkarlara stimul verir. &ldquo;Nax&ccedil;ıvan Muxtar Respublikası Arı&ccedil;ıları&rdquo; İctimai Birliyinin sədri Etibar Məmmədov, D&ouml;vlət Baytarlıq Xidmətinin sektor m&uuml;diri H&uuml;seyn Məmmədov və tədbirdə iştirak edən d&ouml;vlət qurumlarının əməkdaşları m&ouml;vzu ətrafında &ccedil;ıxışlar edərək arı&ccedil;ılıqda məhsuldarlığın artırılmasında vacib mənbələrdən biri kimi arı ailələrinin payızda qışlamaya hazırlanması işinin d&uuml;zg&uuml;n təşkili və arıxanalarda cavan arı analarının olmasının b&ouml;y&uuml;k əhəmiyyətindən danışıblar. Sonda təsərr&uuml;fat&ccedil;ıları maraqlandıran suallar cavablandırılıb, m&uuml;təxəssislər tərəfindən hazırlanan maarifləndirici bukletlər m&uuml;şavirə iştirak&ccedil;ılarına paylanıb.</span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin mətbuat xidməti</strong></span></p>', 1, '2021-12-02 06:15:42', '2021-12-02 06:15:42'),
(29, 'Naxçıvanda “Arıçılıq məhsulları-bal satış yarmarkası” keçirilib', 19, 'news_1638440248.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Muxtar respublikanın iqlim şəraiti, zəngin bitki &ouml;rt&uuml;y&uuml; bu ərazidə qədim zamanlardan bəri insanların arı&ccedil;ılıq təsərr&uuml;fatı ilə məşğul olmasına əlverişli şərait yaratmışdır. Bu g&uuml;n d&ouml;vlət tərəfindən həyata ke&ccedil;irilən aqrar islahatlarda arı&ccedil;ılığın inkişafına x&uuml;susi &ouml;nəm verilir. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 18 noyabr 2016-cı il tarixli Sərəncamı ilə təsdiq edilmiş &ldquo;2017-2022-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın icrası arı&ccedil;ılıq təsərr&uuml;fatlarının sayının ilbəil artmasına səbəb olmuşdur. Nax&ccedil;ıvan şəhərində ke&ccedil;irilən &ldquo;Arı&ccedil;ılıq məhsulları-bal satış yarmarkası&rdquo; da arı&ccedil;ılar &uuml;&ccedil;&uuml;n geniş imkanlar a&ccedil;ır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Oktyabrın 16-da &ldquo;Nax&ccedil;ıvanqala&rdquo; Tarix-Memarlıq Muzey Kompleksində &ldquo;Arı&ccedil;ılıq məhsulları-bal satış yarmarkası&rdquo; ke&ccedil;irilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisinin Sədri Vasif Talıbov satış yarmarkasında iştirak etmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan D&ouml;vlət Universitetinin m&uuml;əllimləri bal s&uuml;zgəci, yeni nəsil pətəklər, vərəmum toplayan, &ccedil;ər&ccedil;ivə teli gərmə, &ccedil;i&ccedil;ək tozu toplayan alətlər, arı&ccedil;ının rahat işləməsi &uuml;&ccedil;&uuml;n istifadə ediləcək yanmaz əlcəklər, yabanı arı tələsi, yem qabları, arı&ccedil;ının iş masası, elektrikli bal isitmə və &ccedil;&ouml;kd&uuml;rmə qazanı, balı s&uuml;zmə aparatı, eləcə də arı məhsullarından hazırlanan dodaq balzamı, gil maskası, əl kremi, m&uuml;xtəlif faizli spreylər, kəklikotulu, nanəli, propolis və m&uuml;xtəlif &ccedil;eşiddə sabunlar haqqında ətraflı məlumat vermişlər.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ali Məclisin Sədri arı&ccedil;ılarla s&ouml;hbət etmiş, bal və arı&ccedil;ılıq məhsullarından almış, onların qayğıları ilə maraqlanmışdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Baxış zamanı məlumat verilmişdir ki, satış yarmarkasına muxtar respublikanın şəhər və rayonlarında fəaliyyət g&ouml;stərən 301 arı&ccedil;ı tərəfindən 17 tondan artıq s&uuml;zmə, 3 tondan artıq şan bal, arı s&uuml;d&uuml;, mum, vərəmum, &ccedil;i&ccedil;ək tozu, g&uuml;ləm, bal arağı və baldan hazırlanmış şirniyyatlar &ccedil;ıxarılmışdır. Nax&ccedil;ıvan balı &ouml;z dadı, ətri, g&ouml;r&uuml;n&uuml;ş&uuml;, həm&ccedil;inin bir sıra keyfiyyət g&ouml;stəricilərinə g&ouml;rə Azərbaycanda, eyni zamanda qonşu &ouml;lkələrdə yaxşı tanınır və y&uuml;ksək qiymətləndirilir. Son illər Nax&ccedil;ıvanda arı&ccedil;ılıq sahəsində aparılan elmi araşdırmaların həcmi &ccedil;oxalmış, bu sahəyə dair kitab və dərsliklər nəşr olunmuşdur. Balla yanaşı, vərəmum, &ccedil;i&ccedil;ək tozu, g&uuml;ləm, arı s&uuml;d&uuml; və digər arı&ccedil;ılıq məhsulları y&uuml;ksək tibbi əhəmiyyətə malikdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan şəhərindən olan arı&ccedil;ı Məhərrəm Eyvazov minnətdarlıq edərək demişdir: &ldquo;Bu g&uuml;n bizim &uuml;&ccedil;&uuml;n ikiqat bayramdır. Tarixi Qələbəni qeyd edirik. Bu qələbənin qazanılmasında Nax&ccedil;ıvandan olan igid oğulların da payı vardır. Bunu &ouml;lkə baş&ccedil;ısı cənab İlham Əliyev bu il mayın 10-da Nax&ccedil;ıvana səfəri zamanı qeyd etmiş, Nax&ccedil;ıvanın s&uuml;rətlə inkişaf etdiyini bildirmişdir. Biz də bu inkişafdan bəhrələnərək təsərr&uuml;fatımızı genişləndiririk&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ldquo;Nax&ccedil;ıvan məhsulları&rdquo; publik h&uuml;quqi şəxsin stendində məlumat verilmişdir ki, stenddə 5 m&uuml;əssisənin 8 məhsulu sərgilənir, m&uuml;xtəlif &ccedil;əkilərdə olan ballardan hədiyyəliklər n&uuml;mayiş etdirilir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">G&uuml;nn&uuml;t kəndindən&nbsp;olan&nbsp;arı&ccedil;ı Fikrət Məmmədov minnətdarlıq edərək demişdir:&nbsp; &ldquo;Bu g&uuml;n arı&ccedil;ılar &uuml;&ccedil;&uuml;n hər c&uuml;r şərait&nbsp; yaradılmışdır. G&uuml;nn&uuml;t kəndinin arı&ccedil;ıları da bu şəraitdən səmərəli istifadə edərək kənd təsərr&uuml;fatı &uuml;&ccedil;&uuml;n əhəmiyyətli olan bu sahəni inkişaf etdirməyə &ccedil;alışırlar&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cəlilkənddən olan arı&ccedil;ı Familə Mirzəyeva demişdir: &ldquo;Muxtar respublikadahəyata ke&ccedil;irilən tədbirlər ailə təsərr&uuml;fat&ccedil;ılarını daha da ruhlandırır, onlar məhsullarını artırırlar. 20 arı ailəm var,1 hektar sahədə sarımsaq əkmişəm. Gələcəkdə 3 hektarda taxıl əkməyi nəzərdə tuturam. Muxtar respublikadaailə təsərr&uuml;fat&ccedil;ıları &uuml;&ccedil;&uuml;n hər c&uuml;r şərait yaradılır, yeni iş yerləri a&ccedil;ılır. Mən dəistifadəyə verildikdən sonra Şərur Sənaye Məhəlləsində istehsal olunacaq qarğıdalı yağına &ouml;z t&ouml;hfəmi vermək &uuml;&ccedil;&uuml;n gələcəkdə eyni zamanda qarğıdalı əkməyi planlaşdırıram&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sələs&uuml;z kəndindən olan arı&ccedil;ı Ehtibar Məmmədov minnətdarlıq edərək demişdir ki, bu il son 10 ilin ən y&uuml;ksək məhsulun g&ouml;t&uuml;rm&uuml;ş&uuml;k. İndi bazarlarda kənd adamlarının istehsal etdikləri yerli məhsullara &uuml;st&uuml;nl&uuml;k verilməsi, həm də satış yarmarkalarının ke&ccedil;irilməsi məhsulumuzun daha &ccedil;ox satılması ilə nəticələnir. Bu da təsərr&uuml;fatımı genişləndirməyə imkan verir. Bir sıra arı&ccedil;ılıq məhsulları ixrac edir, bazarlarda he&ccedil; bir &ccedil;ətinlik &ccedil;əkmədən satışını həyata ke&ccedil;iririk. Artıq yeni layihə əsasında ana arı yetişdirməyə hazırlaşırıq.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Babək rayon Gərmə&ccedil;ataq kənd sakini Mətləb Quliyev &ouml;z təsərr&uuml;fatının inkişafından danışaraq demişdir ki, yaradılan şəraitin nəticəsidir ki, 5-6 il əvvəl 20 arı ailəm olmasına baxmayaraq, hazırda 70 arı ailəsi saxlayıram. Eyni zamanda ucqar dağ kəndi olan Gərmə&ccedil;ataqda quruculuq tədbirlərinin həyata ke&ccedil;irilməsi nəticəsində 3 ailə geri qayıtmış, kənddə hazırda 2 yeni ev inşa olunur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sədərək rayonundan olan arı&ccedil;ı Nadir Bağırov demişdir ki, arı&ccedil;ı sahibkarların dərmanla təmin edilməsi, hər bir arı ailəsinə g&ouml;rə subsidiyanın və arı&ccedil;ılara g&uuml;zəştli kreditlərin verilməsi bu sahənin inkişafına geniş imkanlar a&ccedil;mışdır. Məhsullarımızı rahat şəkildə satır, b&uuml;dcəmizi zənginləşdiririk. Eyni zamanda təsərr&uuml;fat adamlarının əməyi d&ouml;vlətimiz tərəfindən y&uuml;ksək qiymətləndirilir. Bu da məsuliyyətimizi artırır, daha həvəslə işləyirik. G&uuml;n o g&uuml;n olsun, Kərki balının satışını həyata ke&ccedil;irək.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Satış yarmarkasında məhkumların əl işləri, eləcə də qadınların m&uuml;xtəlif &ccedil;eşiddə əl işləri və istehsal etdikləri bal məhsulları da maraqla qarşılanmışdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ordubad rayonundan olan arı&ccedil;ı İsmayıl Əliyev &ouml;z arı&ccedil;ılıq təsərr&uuml;fatında tətbiq etdiyi təcr&uuml;bəni və &ouml;z&uuml;n&uuml;n hazırladığı arı yeşiklərini təqdim etmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ali Məclisin Sədri aid qurumlar tərəfindən bu c&uuml;r təkliflərin dəyərləndirilməsi barədə tapşırıq vermişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Arı&ccedil;ılar Birliyinin sədri Etibar Məmmədovvə digər arı&ccedil;ılarminnətdarlıq edərək demişlər ki, arı&ccedil;ılığa g&ouml;stərilən d&ouml;vlət qayğısı sayəsində muxtar respublikada arı&ccedil;ılıq &ouml;z&uuml;n&uuml;n inkişaf d&ouml;vr&uuml;n&uuml; yaşayır.Bu c&uuml;r tədbirlərin ke&ccedil;irilməsi həm məhsul satışı, həm də arı&ccedil;ılar arasında təcr&uuml;bə m&uuml;badiləsi aparılması baxımından əhəmiyyətlidir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Satış yarmarkasında baldan hazırlanan m&uuml;xtəlif şirniyyatların, eləcə də dulus&ccedil;uluq, ağacişləmə, tikmə n&uuml;munələrinin, ana arı yetişdirmə qutularının, termoplastik pətəklərin, ana arı təcridedici qurğuların, arı&ccedil;ıların hazırladıqları m&uuml;xtəlif əl işlərinin də n&uuml;mayişi və satışı həyata ke&ccedil;irilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ali Məclisin Sədri ailə təsərr&uuml;fatlarına, o c&uuml;mlədən arı&ccedil;ılara kreditlərin verilməsinin davam etdirilməsi, onların təkliflərinin nəzərə alınması, bu sahədə nəşrlərin artırılması, maarifləndirici tədbirlərin davam etdirilməsi və arı&ccedil;ılıq mədəniyyətinin formalaşdırılması barədə tapşırıqlar vermişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Satış yarmarkası oktyabrın 17-də də davam edəcəkdir.</span></p>', 1, '2021-12-02 06:17:28', '2021-12-02 06:17:28'),
(30, '1 noyabr Kənd Təsərrüfatı İşçiləri Günüdür', 19, 'news_1638440418.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Azərbaycan Respublikasının Prezidenti cənab İlham Əliyevin 2008-ci il 9 oktyabr tarixli Fərmanı ilə hər il noyabrın 1-i &ouml;lkəmizdə Kənd Təsərr&uuml;fatı İş&ccedil;iləri G&uuml;n&uuml; kimi qeyd olunur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Uuml;mummilli lider Heydər Əliyevin &ouml;lkəmizə birinci rəhbərliyi d&ouml;vr&uuml;ndə Azərbaycan aqrar sahədə ke&ccedil;miş ittifaqın kənd təsərr&uuml;fatının kompleks inkişaf etdiyi qabaqcıl respublikalardan birinə &ccedil;evrilmişdir. Həmin d&ouml;vrdə &ouml;lkəmizdə kənd təsərr&uuml;fatının elmi əsaslarla inkişafı nəticəsində bu sahədə zəngin təcr&uuml;bə formalaşmışdır. Təəss&uuml;f ki, m&uuml;stəqilliyin ilk illərində &ouml;lkədə baş verən &ouml;zbaşınalıq nəticəsində aqrar sahədə yaradılan bu infrastruktur da tamamilə dağıdılmışdır. Həmin d&ouml;vrdə Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisinin Sədri vəzifəsində işləyən &uuml;mummilli lider isə kənd təsərr&uuml;fatının inkişafının vacibliyini daim &ouml;n plana &ccedil;əkmiş, bununla bağlı m&uuml;h&uuml;m qərarlar qəbul etmişdir. Ali Məclisin 1992-ci il aprelin 6-da ke&ccedil;irilən sessiyasında &ldquo;Nax&ccedil;ıvan Muxtar Respublikasında zərərlə işləyən kolxoz və sovxozlar haqqında&rdquo; və &ldquo;Rentabelli işləyən kolxoz və sovxozların ictimai mal-qarasının &ouml;zəlləşdirilməsi barədə təkliflər haqqında&rdquo; qərarlar qəbul edilmiş, bununla da Azərbaycanda torpaq islahatına ilk dəfə Nax&ccedil;ıvandan başlanılmış, &ouml;zəl m&uuml;lkiyyətə əsaslanan aqrar m&uuml;nasibətlər formalaşmışdır. 1993-2003-c&uuml; illərdə isə &uuml;mummilli lider Heydər Əliyevin rəhbərliyi ilə &ouml;lkəmizdə bu istiqamətdə kompleks tədbirlər g&ouml;r&uuml;lm&uuml;ş, uğurlu islahatlar həyata ke&ccedil;irilmiş, qanunvericilik bazası yaradılmışdır. Ulu &ouml;ndər&nbsp;&nbsp;demişdir: &ldquo;Azərbaycanın kənd təsərr&uuml;fatı potensialı &ndash; torpaqlarımız, hava, su kənd təsərr&uuml;fatı məhsulları istehsalının s&uuml;rətlə artırılmasına y&ouml;nəldilməli, bundan səmərəli istifadə olunmalıdır&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ulu &ouml;ndərin m&uuml;əyyənləşdirdiyi aqrar islahatlar bu g&uuml;n &ouml;lkə baş&ccedil;ısı cənab İlham Əliyev tərəfindən uğurla davam etdirilir, kənd təsərr&uuml;fatının inkişafı diqqətdə saxlanılır, ərzaq təhl&uuml;kəsizliyinin təmin olunması istiqamətində ardıcıl tədbirlər g&ouml;r&uuml;l&uuml;r. Azərbaycan Respublikasının Prezidenti cənab İlham Əliyev demişdir: &ldquo;Aqrar sahə bizim &uuml;&ccedil;&uuml;n strateji sahədir. Bu, həm iqtisadi, həm sosial sahədir... Ona g&ouml;rə son illərdə kənd təsərr&uuml;fatının, regionların sosial-iqtisadi inkişafına g&ouml;stərilən diqqət məhz bu məqsədi g&uuml;d&uuml;r ki, həm məşğulluq, həm məhsuldarlıq artsın və Azərbaycan həm &ouml;z daxili tələbatını, həm də ixrac edilən məhsulları ən y&uuml;ksək səviyyəyə qaldırsın&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikasında da iqtisadiyyatın b&uuml;t&uuml;n sahələri kimi, kənd təsərr&uuml;fatının inkişafı daim diqqət mərkəzində saxlanılır. Aparılan islahatlar bitki&ccedil;ilik və heyvandarlıq, eləcə də emal sənayesinin inkişafı &uuml;&ccedil;&uuml;n geniş imkanlar a&ccedil;mışdır. 2000-ci ildə muxtar respublikada torpaq islahatı başa &ccedil;atdırılmış, 72 min 758 ailəyə 56 min 372 hektar torpaq sahəsi verilmişdir. Bundan sonra kənd təsərr&uuml;fatının inkişafında əsaslı d&ouml;n&uuml;ş yaradılmış, bol məhsul istehsalının əsası qoyulmuşdur. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin sərəncamları ilə təsdiq edilmiş d&ouml;vlət proqramları muxtar respublikada aqrar sahənin inkişafına m&uuml;h&uuml;m təkan vermiş, kənd təsərr&uuml;fatının ayrı-ayrı sahələri &uuml;zrə uğurlu nəticələr əldə olunmuşdur. Qəbul edilən d&ouml;vlət proqramlarının icrası istiqamətində həyata ke&ccedil;irilən tədbirlər məhsul bolluğuna zəmin yaratmış, təsərr&uuml;fatın m&uuml;xtəlif sahələr &uuml;zrə fəaliyyətini şaxələndirmişdir.Muxtar respublikada əhalinin əsas hissəsinin kənd təsərr&uuml;fatı ilə məşğul olması burada əkin&ccedil;iliyin inkişafını şərtləndirir. Fermerlərə əkinlərə g&ouml;rə subsidiyaların verilməsi, texniki təminatın yaxşılaşdırılması &uuml;&ccedil;&uuml;n istehsal&ccedil;ılara lizinq yolu ilə g&uuml;zəştli şərtlər əsasında texnika və g&uuml;brələrin satılması əkin&ccedil;iliyin inkişafında yeni istiqamətlər a&ccedil;mış, əkin sahələrinin və məhsuldarlığın ilbəil artmasına səbəb olmuşdur. 2021-ci ilin məhsulu &uuml;&ccedil;&uuml;n muxtar respublikada 64 min 259 hektar sahədə əkin aparılmışdır. Cari ilin 9 ayı ərzində muxtar respublikada 105 min 807 ton dənli və dənli-paxlalı, 11 min 44 ton dən &uuml;&ccedil;&uuml;n qarğıdalı, 395 ton dən &uuml;&ccedil;&uuml;n g&uuml;nəbaxan, 71,8 ton t&uuml;t&uuml;n, 77 min 470,1 ton tərəvəz, 54 min 689 ton kartof, 33 min 615 ton bostan məhsulları, 41 min 299,2 ton meyvə, 10 min 158,4 ton &uuml;z&uuml;m istehsal olunmuşdur. Bir ne&ccedil;ə məhsul n&ouml;v&uuml; &uuml;zrə yığım hazırda davam etdirilir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Su təminatının yaxşılaşdırılması kənd təsərr&uuml;fatının inkişafı ilə yanaşı, torpaq ehtiyatlarının qorunmasına da şərait yaradır. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 2020-ci il 27 noyabr tarixli Sərəncamı ilə təsdiq olunmuş &ldquo;2021-2025-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında kənd təsərr&uuml;fatı təyinatlı torpaqların m&uuml;nbitliyinin artırılmasına dair&rdquo; Tədbirlər Planına uyğun olaraq bu sahədə işlər davam etdirilir. Torpaq sahələrinin normalara uyğun suvarma suyu ilə təchizatı əkin&ccedil;iliyin inkişafını təmin edən əsas amillərdən biridir. Buna g&ouml;rə də muxtar respublikada irriqasiya sistemləri daim yeniləşdirilir, qapalı suvarma şəbəkələri qurulur, meliorasiya tədbirləri həyata ke&ccedil;irilir. Bu da torpaq sahələrində su təminatının yaxşılaşdırılmasına, yeni torpaq sahələrinin əkin d&ouml;vriyyəsinə qatılmasına, eyni zamanda məşğulluğun təmin olunmasına imkan verir. Cari ilin &ouml;tən d&ouml;vr&uuml;ndə meliorasiya və irriqasiya sistemlərinin yenidən qurulması diqqətdə saxlanılmış, Babək rayonunun Nehrəm kəndi ərazisində 1170, Culfa rayonunun Bənəniyar kəndi ərazisində 1050, Şahbuz rayonunun T&uuml;rkeş, Sələs&uuml;z, Mərəlik kəndlərinin ərazilərində 618, Kəngərli rayonunun B&ouml;y&uuml;kd&uuml;z, Xok, Yurd&ccedil;u kəndləri ərazilərində 2690 hektar torpaq sahəsində qapalı suvarma şəbəkələrinin tikintisi başa &ccedil;atdırılmışdır. Kəngərli rayonunun B&ouml;y&uuml;kd&uuml;z kəndi ərazisində yeni qapalı suvarma şəbəkələrinin tikintisi davam etdirilir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ldquo;2021-2025-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında meyvə&ccedil;iliyin və tərəvəz&ccedil;iliyin inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın icrası ilə əlaqədar olaraq Kənd Təsərr&uuml;fatı Nazirliyi tərəfindən əhalinin meyvə və tərəvəz məhsullarına olan tələbatının yerli istehsal hesabına təmin edilməsi məqsədilə meyvə&ccedil;iliyin inkişafı ilə bağlı m&uuml;vafiq tədbirlər həyata ke&ccedil;irilir. Son illər əkin&ccedil;ilik &uuml;&ccedil;&uuml;n yararlı olmayan torpaq sahələrində iməciliklər yolu ilə salınan yeni meyvə bağları muxtar respublikanın yaşıl &ouml;rt&uuml;y&uuml;n&uuml; zənginləşdirməklə yanaşı, həm də əhalinin meyvə məhsullarına olan tələbatının &ouml;dənilməsi &uuml;&ccedil;&uuml;n geniş imkanlar a&ccedil;ır. Hər il meyvə bağlarından tonlarla məhsul yığılır. Yeni və ənənəvi meyvə sortlarından ibarət bağların salınması da davam etdirilir. Cari ilin &ouml;tən d&ouml;vr&uuml;ndə muxtar respublikada, &uuml;mumilikdə 64,3 hektar sahədə yeni meyvə bağları salınmış, 67 min 399 m&uuml;xtəlif meyvə tingi, 11 min 190 &uuml;z&uuml;m kolu əkilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Muxtar respublikada həyata ke&ccedil;irilən sosial-iqtisadi siyasətin prioritet istiqamətlərindən birini əhalinin keyfiyyətli və təmiz ərzaq məhsulları ilə təmin olunması təşkil edir. Bu istiqamətdə g&ouml;r&uuml;lən işlər ərzaq təhl&uuml;kəsizliyinin g&uuml;cləndirilməsi &uuml;&ccedil;&uuml;n vacib əhəmiyyətə malikdir. Heyvandarlıq aqrar sektorun əsas sahələrindən olmaqla, əhalinin ərzaq məhsulları ilə təmin edilməsində x&uuml;susi əhəmiyyət kəsb edir. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 2020-ci il 17 fevral tarixli Sərəncamı ilə təsdiq edilmiş &ldquo;2020-2025-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında baytarlıq təbabətinin inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın da əsas məqsədlərindən biri heyvan mənşəli qida məhsullarının ilkin istehsalı, tədar&uuml;k&uuml;, emalı, qablaşdırılması, saxlanılması, daşınması, d&ouml;vriyyəsi də daxil olmaqla, qida zəncirinin b&uuml;t&uuml;n mərhələlərində məhsulların təhl&uuml;kəsizliyinin təmin edilməsidir. Hazırda muxtar respublikada iribuynuzlu mal-qaranın baş sayı 125 min 426-a, xırdabuynuzlu heyvanların sayı isə 771 min 372-ə &ccedil;atdırılmışdır. Cari ilin &ouml;tən d&ouml;vr&uuml;ndə 41 heyvandarlıq, 2 quş&ccedil;uluq təsərr&uuml;fatı yaradılmış, 14 heyvandarlıq və 1 quş&ccedil;uluq təsərr&uuml;fatı sahəsinin genişləndirilməsi işləri başa &ccedil;atdırılmışdır. 20 heyvandarlıq təsərr&uuml;fatının yaradılması və 1 heyvandarlıq kompleksinin yenidən qurulması davam etdirilir. Bank və kredit təşkilatları tərəfindən kənd təsərr&uuml;fatı ilə məşğul olan təsərr&uuml;fat subyektlərinə, &uuml;mumilikdə, 13 milyon 500 min manat g&uuml;zəştli kredit verilmişdir ki, bunun da 11 milyon 6 min 100 manatı heyvandarlığın inkişafı &uuml;&ccedil;&uuml;n ayrılmışdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Muxtar respublikada heyvandarlığın cins tərkibinin yaxşılaşdırılması və məhsuldarlığın y&uuml;ksəldilməsi məqsədilə Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 2019-cu il 21 sentyabr tarixli Sərəncamı ilə Nax&ccedil;ıvan Muxtar Respublikasının S&uuml;ni Mayalanma Mərkəzi yaradılmış və mərkəzdə yeni infrastruktur obyektlərinin tikintisi başa &ccedil;atdırılaraq istifadəyə verilmişdir. Bu sahədə həyata ke&ccedil;irilən tədbirlərin nəticəsidir ki, bu il heyvandarlıq təsərr&uuml;fatlarında s&uuml;ni yolla 10 min 74 baş inək, d&uuml;yə və camış mayalandırılmış, əvvəlki d&ouml;vrdə mayalandırılmış inək, d&uuml;yə və camışlardan 7 min 168 baş sağlam bala alınmışdır. S&uuml;ni Mayalanma Mərkəzinin fəaliyyətə başlaması heyvandarlığın cins tərkibinin yaxşılaşdırılması istiqamətində daha geniş imkanlar a&ccedil;acaqdır. Əhalinin balıq əti ilə təmin olunması istiqamətində də əsaslı işlər g&ouml;r&uuml;lm&uuml;şd&uuml;r. Hazırda muxtar respublikada balıq yetişdirilməsi ilə bağlı 16 təsərr&uuml;fat fəaliyyət g&ouml;stərir. G&ouml;r&uuml;lm&uuml;ş işlərin nəticəsidir ki, 2021-ci ilin 9 ayı ərzində diri &ccedil;əkidə ət istehsalı 20 min 118,6 ton, s&uuml;d istehsalı 74 min 712,2 ton, yun istehsalı 925,2 ton, yumurta istehsalı isə 89 milyon 771 min 600 ədəd olmuşdur. Əhalini sağlam heyvandarlıq məhsulları ilə təmin etmək &uuml;&ccedil;&uuml;n D&ouml;vlət Baytarlıq Xidməti tərəfindən də tədbirlər həyata ke&ccedil;irilir. Bu məqsədlə həyata ke&ccedil;irilən sistemli tədbirlər nəticəsində qida məhsullarının, x&uuml;susilə ət və s&uuml;d məhsullarının y&uuml;ksək keyfiyyət g&ouml;stəricilərinin təmin olunması, heyvan mənşəli xəstəliklərə qarşı vaxtında m&uuml;barizə tədbirlərinin aparılması prosesləri uğurla davam etdirilir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Ouml;lkəmizin bir &ccedil;ox b&ouml;lgələrində olduğu kimi, Nax&ccedil;ıvanda da arı&ccedil;ılıq aqrar istehsalın davamlılığını və səmərəliliyini təmin edən gəlirli sektorlardan biridir. Qədim diyarımızda kifayət qədər nektarlı bitki n&ouml;vlərinin olması burada arı&ccedil;ılığın inkişafına səbəb olmuşdur. Bu sahənin yeni inkişaf mərhələsinə qədəm qoymasında Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 2016-cı il 18 noyabr tarixli Sərəncamı ilə təsdiq edilmiş &ldquo;2017-2022-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın m&uuml;h&uuml;m rolu var. Proqramın uğurlu icrası arı ailələrinin sayının artmasına və bu sahədə &uuml;mumi məhsuldarlığın daha da y&uuml;ksəldilməsinə şərait yaradır. Muxtar respublikada 99 min 935 arı ailəsi vardır. İstehsal olunan balın satışı &uuml;&ccedil;&uuml;n şəraitin yaradılması və ənənəvi olaraq &ldquo;Arı&ccedil;ılıq məhsulları &ndash; bal festivalı&rdquo;nın və satış yarmarkasının ke&ccedil;irilməsi arı ailələrinin &ccedil;oxalmasına, bal istehsalının artmasına və bu sahədə sahibkarlığın inkişafına stimul olmuşdur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kənd təsərr&uuml;fatının bitki&ccedil;ilik sahəsində əldə olunan uğurlarda sahibkarların m&uuml;asir kənd təsərr&uuml;fatı texnikaları ilə təchiz olunmasının b&ouml;y&uuml;k rolu vardır. Hazırda muxtar respublikada sahibkarlara aqroservis xidməti &ldquo;Nax&ccedil;ıvan Aqrolizinq&rdquo; A&ccedil;ıq Səhmdar Cəmiyyətinin rayonlarda olan kənd təsərr&uuml;fatı maşın və aqreqatları ilə yanaşı, həm də şəxsi texnikalar vasitəsilə həyata ke&ccedil;irilir. &Ouml;tən d&ouml;vrdə &ldquo;Nax&ccedil;ıvan Aqrolizinq&rdquo; A&ccedil;ıq Səhmdar Cəmiyyətinin xətti ilə 3386 m&uuml;xtəlif təyinatlı kənd təsərr&uuml;fatı texnikası gətirilmiş, onlardan 2554-&uuml; nağd hesablaşma və lizinq yolu ilə sahibkarlara satılmışdır. Alınmış texnika və aqreqatların 311-i cari ilin 9 ayında gətirilmişdir. M&ouml;vcud texnikalar kənd təsərr&uuml;fatı sahəsində aqrotexniki tədbirləri keyfiyyətlə həyata ke&ccedil;irməyə imkan verir. Fermerlərə lizinq yolu ilə m&uuml;asir tələblərə cavab verən yeni kənd təsərr&uuml;fatı texnikalarının alınıb icarəyə verilməsi ilə bağlı g&uuml;zəştlərin tətbiqi isə bu sahədə fəaliyyətin səmərəli təşkilinə hesablanmışdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Əkin&ccedil;ilikdə məhsuldarlığın y&uuml;ksəldilməsi &uuml;&ccedil;&uuml;n vacib amillərdən biri də torpaqların qida maddələri ilə təminolunma dərəcəsinin m&uuml;əyyən edilməsidir. Bu istiqamətdə də muxtar respublikada m&uuml;h&uuml;m tədbirlər həyata ke&ccedil;irilir. Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin struktur b&ouml;lmələri olan Akademik Həsən Əliyev adına &ldquo;Araz&rdquo; Elm İstehsalat Birliyi, Toxum&ccedil;uluq və Bitkilərin M&uuml;hafizəsi İdarəsində yaradılan və y&uuml;ksək standartlara cavab verən m&uuml;asir avadanlıqlarla təchiz edilmiş laboratoriyalarda əkin sahələrindən g&ouml;t&uuml;r&uuml;lm&uuml;ş torpaq n&uuml;munələri və toxum materialları analiz edilir. Kənd təsərr&uuml;fatının inkişafı istiqamətində həyata ke&ccedil;irilən tədbirlərin sırasında strateji əhəmiyyətli istehsal sahələrindən olan toxum&ccedil;uluq da diqqətdə saxlanılır. Hazırda muxtar respublikada toxuma olan tələbatı &ouml;dəmək və toxumluq məhsulların istehsalını artırmaq məqsədilə ardıcıl tədbirlər g&ouml;r&uuml;l&uuml;r, yeni toxum&ccedil;uluq təsərr&uuml;fatları yaradılır, iqlim və torpaq x&uuml;susiyyətlərinə uyğun məhsuldar toxum sortları yetişdirilir. Muxtar respublikada məhsuldar və keyfiyyətli toxum n&ouml;vləri istehsalının stimullaşdırılması məqsədilə toxum&ccedil;uluq təsərr&uuml;fatlarının maddi-texniki bazasının m&ouml;hkəmləndirilməsi istiqamətində işlər davam etdirilir. Nax&ccedil;ıvan şəhərində &ldquo;Bərəkət Toxum&ccedil;uluq&rdquo; Məhdud Məsuliyyətli Cəmiyyəti &uuml;&ccedil;&uuml;n yeni kompleksin istifadəyə verilməsi tədbirində Ali Məclisin Sədri cənab Vasif Talıbov demişdir: &ldquo;D&uuml;nyada hansı proseslərin baş verməsindən asılı olmayaraq, kənd təsərr&uuml;fatı m&uuml;tləq inkişaf etdirilməlidir. Bu g&uuml;n d&uuml;nyada o &ouml;lkələr pandemiya ilə rahat m&uuml;barizə apara bilirlər ki, həmin &ouml;lkələrdə ərzaq təhl&uuml;kəsizliyi təmin olunub. Kənd təsərr&uuml;fatı iqtisadiyyatın lokomotividir. Ona g&ouml;rə də kənd təsərr&uuml;fatı inkişaf etdirilməlidir ki, &ouml;lkədə digər sahələrin inkişafı da təmin olunsun. Muxtar respublikada ərzaq təhl&uuml;kəsizliyinin təmin olunması sahəsində g&ouml;r&uuml;lən işlər, kənd təsərr&uuml;fatının inkişafı qarşıdakı illərdə də məhsul bolluğu yaradacaqdır. Məqsəd ondan ibarətdir ki, kənd təsərr&uuml;fatı sahəsində əhalinin məşğulluğu təmin olunsun, məhsul istehsalı daha da artsın&rdquo;. Hazırda muxtar respublikada 7 toxum&ccedil;uluq təsərr&uuml;fatı fəaliyyət g&ouml;stərir. Sahibkarlara elit, I və II reproduksiyalı buğda və arpa toxumlarının satışının təşkil edilməsi, eləcə də 70 faiz g&uuml;zəştli şərtlərlə mineral g&uuml;brələrin verilməsi təmin olunur. &Ouml;tən d&ouml;vrdə məhsul istehsal&ccedil;ıları 7 min 542,3 ton mineral g&uuml;brə ilə təmin olunmuşdur. İstehsal olunan I və II reproduksiyalı toxumların satışına g&ouml;rə d&ouml;vlət tərəfindən subsidiyaların &ouml;dənilməsi, həm&ccedil;inin əkin sahəsinin hər hektarına fermerlərə 60 manat yardım verilməsi taxıl istehsalına marağı daha da artırmışdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Əhalini ilboyu meyvə-tərəvəz məhsulları ilə təmin etmək muxtar respublikada aqrar sahədə həyata ke&ccedil;irilən əsas tədbirlərdəndir. İritutumlu soyuducu anbarların tikilməsi və istixana komplekslərinin yaradılması ilin istənilən fəslində bazarın tələbatının &ouml;dənilməsinə imkan verir. Hazırda muxtar respublikada &uuml;mumi tutumu 21 min 150 ton olan 49 soyuducu anbar və sahəsi 150 min 817 kvadratmetr olan 34 istixana kompleksi fəaliyyət g&ouml;stərir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Muxtar respublikada əhalinin ərzaq məhsullarına olan tələbatının etibarlı &ouml;dənilməsi istiqamətində əlavə tədbirlər də g&ouml;r&uuml;l&uuml;r, istehsal olunan malların satışı &uuml;&ccedil;&uuml;n hərtərəfli şərait yaradılır. Kənd təsərr&uuml;fatının dayanıqlı inkişafının təmin olunması və istehsal olunan məhsulların ixrac potensialının artırılması ilə kənd təsərr&uuml;fatı istehsalına əsaslanan emal m&uuml;əssisələrinin qurulması məqsədilə &ldquo;2021-2025-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında kənd təsərr&uuml;fatı məhsullarının istehsalı, emalı və ixracının stimullaşdırılmasına dair D&ouml;vlət Proqramı&rdquo; təsdiq edilmişdir. Artan tələbatın qarşılanmasında, istehlak sahəsində əl&ccedil;atanlığın təmin olunmasında istehsal anlayışı m&uuml;stəsna əhəmiyyətə malikdir. Məhz bu baxımdan təsdiq olunan D&ouml;vlət Proqramının b&ouml;y&uuml;k əhəmiyyəti vardır. Artıq bir ne&ccedil;ə ildir ki, istehsal&ccedil;ıların istehsal etdikləri məhsulların satışını təmin etmək &uuml;&ccedil;&uuml;n kənd təsərr&uuml;fatı məhsullarının satış yarmarkaları təşkil edilir. B&uuml;t&uuml;n bunlar əhalinin ərzaq məhsulları ilə etibarlı təmin olunmasında həlledici rol oynayır. Etibarlı ərzaq təminatının yaradılması hesabına ixrac potensialı da artırılmışdır. 2021-ci ilin yanvar-sentyabr aylarında muxtar respublikada 409 milyon 830 min 300 manatlıq kənd təsərr&uuml;fatı məhsulları istehsal olunmuşdur ki, bu da 2020-ci ilin m&uuml;vafiq d&ouml;vr&uuml; ilə m&uuml;qayisədə 3,5 faiz &ccedil;oxdur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Aqrar sahənin inkişafı muxtar respublikada ailə təsərr&uuml;fatlarının fəaliyyəti &uuml;&ccedil;&uuml;n də geniş imkanlar a&ccedil;ır. İstər məhsul istehsalı və satışı &uuml;&ccedil;&uuml;n yaradılan şərait, istərsə də texnika təminatı və d&ouml;vlət maliyyə dəstəyinin g&ouml;stərilməsi ailə təsərr&uuml;fatlarının sayının ilbəil artmasına səbəb olmuşdur. Bu g&uuml;n muxtar respublikada ailə təsərr&uuml;fatlarının əksər hissəsinin aqrar sahə &uuml;zrə ixtisaslaşması daxili bazarın keyfiyyətli yerli məhsullarla təminatına &ouml;z t&ouml;hfəsini verir. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisinin Sədri cənab Vasif Talıbovun dediyi kimi: &ldquo;Su təminatı, əkinəyararlı və bərəkətli torpaqlar olan yerdə inkişaf təmin olunur və ərzaq bolluğu yaranır. &Ccedil;&uuml;nki &ouml;lkə iqtisadiyyatının əsasını kənd təsərr&uuml;fatı təşkil edir. Əgər bu sahədə işlər d&uuml;zg&uuml;n qurularsa, o zaman iqtisadiyyat da inkişaf edəcəkdir. İqtisadiyyatın lokomotivi isə kənd təsərr&uuml;fatıdır&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kənd Təsərr&uuml;fatı Nazirliyinin</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">mətbuat xidməti</span></p>', 1, '2021-12-02 06:20:18', '2021-12-02 06:20:18');
INSERT INTO `news` (`id`, `title`, `category_id`, `image`, `content`, `status`, `created_at`, `updated_at`) VALUES
(31, 'Naxçıvanda “Ailə təsərrüfatı məhsulları” satış yarmarkası keçirilib.', 19, 'news_1643694667.jpg', '<p><span style=\"font-size:16px\">Ailə təsərr&uuml;fatları daxili bazarın keyfiyyətli yerli məhsullarla təminatı ilə yanaşı, &ouml;z&uuml;n&uuml;məşğulluğun təmin olunmasına və iqtisadi inkişafa da m&uuml;h&uuml;m t&ouml;hfə verir. Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin 2019-cu il 18 yanvar tarixli Sərəncamı ilə təsdiq edilən&nbsp;&ldquo;Nax&ccedil;ıvan Muxtar Respublikasında ailə təsərr&uuml;fatlarının inkişafı ilə bağlı Tədbirlər Planı&rdquo;nın icrası bu baxımdan m&uuml;h&uuml;m əhəmiyyətə malik olub, ailə təsərr&uuml;fatlarına geniş fəaliyyət imkanları a&ccedil;ılıb. Bu sahənin inkişafı istiqamətində g&ouml;r&uuml;lən ardıcıl tədbirlərin nəticəsidir ki, ailə təsərr&uuml;fatlarının sayı getdikcə artır, ailə biznesi genişlənir.Noyabrın 20-də &ldquo;Nax&ccedil;ıvanqala&rdquo; Tarix-Memarlıq Muzey Kompleksində &ldquo;Ailə təsərr&uuml;fatı məhsulları&rdquo; satış yarmarkası ke&ccedil;irilib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisinin Sədri Vasif Talıbov yarmarkada iştirak edib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Yarmarkaya Şərur, Babək, Ordubad, Culfa, Kəngərli, Şahbuz və Sədərək rayonlarından, eləcə də Nax&ccedil;ıvan şəhərindən &uuml;mumilikdə, 453 ailə təsərr&uuml;fatı tərəfindən 540 &ccedil;eşiddə 41 mindən &ccedil;ox qablaşdırılmış məhsul &ccedil;ıxarılıb.&nbsp;Nax&ccedil;ıvan qovurması, xaması, qaymağı, pendiri, qatığı, m&uuml;xtəlif n&ouml;v m&uuml;rəbbə və şirniyyatları, alana, sucuq, cəviz, badam, qurut, m&uuml;xtəlif &ccedil;eşidli meyvə şirələri, doşabları, sirkələri, şərablar və meyvə araqları, eləcə də turşular, kompotlar, cemlər, meyvə-tərəvəz quruları,&nbsp;milli mətbəximizə aid &ccedil;&ouml;rək &ccedil;eşidləri və digər məhsullar yarmarka iştirak&ccedil;ılarına təqdim edilib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nax&ccedil;ıvan torpağı yabanı tərəvəz və dərman bitkiləri ilə zəngindir. Tarixən həmin sərvətlərdən məişətdə və xalq təbabətində geniş istifadə olunub. Bu g&uuml;n də kəndlərdə yabanı tərəvəz və dərman bitkilərinin istehsalı sahəsində ixtisaslaşan&nbsp;ailə təsərr&uuml;fatları fəaliyyət g&ouml;stərir. Yarmarkaya bu sahə ilə məşğul olan ailə təsərr&uuml;fatları tərəfindən dərman bitkiləri və onlardan hazırlanan məhsullar da &ccedil;ıxarılıb.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ailə təsərr&uuml;fatları məhsullarının tara satışı mağazasının stendində məlumat verilib&nbsp;ki, yerli istehsal olan &ccedil;&ouml;lməklər, şirniyyat qəlibləri, meyvə quruları &uuml;&ccedil;&uuml;n qablar və digər &ccedil;eşidlər olmaqla, 34 n&ouml;vdə tara n&uuml;mayiş olunur.&nbsp;Azərbaycan Milli Elmlər Akademiyasının Nax&ccedil;ıvan B&ouml;lməsinə məxsus stenddə Bioresurslar İnstitutu tərəfindən hazırlanan&nbsp;m&uuml;xtəlif &ccedil;eşiddə meyvə quruları, g&uuml;lab, &ccedil;aytikanı yağı, propolis (bərəmum), paketlənmiş zəfəran qurusu, alma sirkəsi və bir sıra digər məhsullar yer alırdı.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ali Məclisin Sədri məhsul &ccedil;eşidlərinin artırılması və istehsal&nbsp; sahəsinin qurulması barədə tapşırıqlar verib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ali Məclisin Sədri yarmarkada &ouml;z&uuml;n&uuml;məşğulluğa cəlb olunan&nbsp;ailə təsərr&uuml;fatlarının məhsullarına baxıb. Məlumat verilib&nbsp;ki, muxtar respublikada 2017-ci ildən &ouml;z&uuml;n&uuml;məşğulluq proqramının icrasına başlanılıb. Həmin g&uuml;ndən 899 ailənin &ouml;z&uuml;n&uuml;məşğulluğu təmin edilib&nbsp;ki, onların ailə &uuml;zvlərinin sayı 4133 nəfərdir. 860 ailə kənd təsərr&uuml;fatı, 22 ailə xidmət, 17 ailə isə istehsal sahələri &uuml;zrə proqrama cəlb edilib. 283 ailənin təsərr&uuml;fatındakı aktivlər nəzarət m&uuml;ddəti bitdiyindən onların m&uuml;lkiyyətinə ke&ccedil;irilib. Bug&uuml;nədək iri və xırdabuynuzlu heyvandarlıq təsərr&uuml;fatında 1500-dək artım qeydə alınıb. 30 ailə n&uuml;munəvi təsərr&uuml;fat sahibi kimi həvəsləndirici tədbirlərə cəlb olunaraq &ouml;z se&ccedil;imləri əsasında əlavə aqreqatlarla təmin olunublar. Yarmarkaya da &ouml;z&uuml;n&uuml;məşğulluğa cəlb olunan&nbsp;ailələr tərəfindən m&uuml;xtəlif &ccedil;eşiddə məhsullar &ccedil;ıxarılıb.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Zəngin təbiəti, təmiz havası və suyu, ekoloji saf məhsulları qədim diyarda meyvəqurutma sahəsində ailə təsərr&uuml;fatlarının formalaşmasına əlverişli şərait yaradır. Yerli meyvələrin qurudulması, onlardan m&uuml;xtəlif m&uuml;rəbbə və şirələrin hazırlanması&nbsp; ənənəsi bu g&uuml;n də yaşadılır. Yarmarkada&nbsp;ailə təsərr&uuml;fatları tərəfindən qablaşdırılan&nbsp;tut, al&ccedil;a, əncir, ərik, gavalı, qızılg&uuml;l və digər meyvə və tərəvəz quruları, eləcə də meyvə şirələri keyfiyyəti ilə se&ccedil;ilirdi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nax&ccedil;ıvanda heyvandarlıq və maldarlığın inkişafı ət və s&uuml;d məhsulları istehsalının geniş yayılmasına səbəb olub. Mal-qaranın cins tərkibinin yaxşılaşdırılması ailə təsərr&uuml;fatlarında istehsal olunan bu məhsulların həcmini və keyfiyyətini artırmışdır. Yarmarkada ailə təsərr&uuml;fatlarında istehsal edilən m&uuml;xtəlif ət və ət məhsulları, qaymaq, xama, qatıq, şor, qurut və digər ənənəvi s&uuml;d məhsulları alıcı marağına səbəb olurdu.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">D&ouml;vlət proqramlarının qəbul olunması və uğurla icra edilməsi&nbsp; nəticəsində muxtar respublikada meyvə&ccedil;iliyin və &uuml;z&uuml;m&ccedil;&uuml;l&uuml;y&uuml;n inkişafına geniş imkanlar a&ccedil;mışdır. Bu sahə &uuml;zrə ixtisaslaşan ailə təsərr&uuml;fat&ccedil;ıları artıq meyvə ilə yanaşı, baldırğan, kişmiş, bal, portağal, tut, alma, armud, g&ouml;ycə, al&ccedil;a, şaftalı araqları, m&uuml;xtəlif n&ouml;v şərablar, sirkələr və doşablar da istehsal edir. Yarmarkada alıcılar bu məhsullara daha &ccedil;ox maraq g&ouml;stərirdi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bu g&uuml;n muxtar respublikada istehsal olunan keyfiyyətli məhsullar &ouml;lkəmizdə və d&uuml;nyada brend məhsul kimi qəbul olunur. X&uuml;susilə ailə təsərr&uuml;fatlarında m&ouml;vs&uuml;mə uyğun mətbəx n&uuml;munələrimizin hazırlanaraq qablaşdırılması və satışa &ccedil;ıxarılması b&ouml;y&uuml;k maraq doğurur. Yarmarkaya m&uuml;xtəlif həcmli taralarda qablaşdırılan&nbsp;Nax&ccedil;ıvan qovurması &ccedil;ıxarılıb.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Biznes Mərkəzinin stendində duzdan və digər məhsullardan hazırlanan&nbsp;əl işləri n&uuml;mayiş etdirilib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&ldquo;Nax&ccedil;ıvan məhsulları&rdquo; publik h&uuml;quqi şəxsin stendində məlumat verilib&nbsp;ki, 4 m&uuml;xtəlif &ccedil;eşiddə hədiyyəliklər, qablaşdırılan&nbsp;bal, 7 n&ouml;vdə kompot, 2 n&ouml;vdə cem, bitki quruları, turşu, hədiyyəlik &ccedil;anta və ramka bal n&uuml;mayiş etdirilir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ailə Təsərr&uuml;fatları İctimai Birliyinin sədri M&uuml;bariz Şirəliyev deyib&nbsp;ki, G&uuml;nn&uuml;t yolu &uuml;zərində 60 hektar ərazidə şum işləri g&ouml;r&uuml;l&uuml;b, 55 hektar ərazidə taxıl əkini nəzərdə tutulub&nbsp;ki, onun da 40 hektarı əkilib, 5 hektar ərazidə yaz əkini planlaşdırılıb. Onun 1 hektarında xaşa, 4 hektarında bostan bitkiləri əkiləcəkdir. 1,5 hektar ərazidə t&uuml;t&uuml;n əkilib&nbsp;ki, 4 tondan artıq quru t&uuml;t&uuml;n tədar&uuml;k olunub. 15 hektar sahədən 105 ton taxıl, 2 hektar kartof sahəsindən 74 ton kartof, 15 hektar qarğıdalı sahəsindən 180 ton qarğıdalı tədar&uuml;k olunub. M&uuml;asir suvarma metodlarından istifadə etməklə bol məhsul əldə edirik.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ali Məclisin Sədri ailə təsərr&uuml;fatlarının sahibləri ilə s&ouml;hbət edib, məhsullardan alıb.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Şərur rayonunun D&uuml;dəngə kəndindən olan Faiq Ağayev deyib&nbsp;ki, d&ouml;vlət qayğısını daim &ouml;z &uuml;zərimizdə hiss edirik. Qəbul olunan sərəncamlar, d&ouml;vlət proqramları bizi daha həvəslə işləməyə s&ouml;vq edir. Bu il qarğıdalı sahəsindən 120-130, taxıl sahəsindən 80 sentner, hektardan 40 ton kartof, 40 ton soğan məhsulu g&ouml;t&uuml;rm&uuml;şəm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Şahbuz rayonunun Badamlı qəsəbəsindən olan ailə təsərr&uuml;fat&ccedil;ısı V&uuml;sal Salahov yaradılan şəraitə g&ouml;rə minnətdarlıq edərək deyib&nbsp;ki, cəviz, &ccedil;&ouml;rəkotu, balqabaq dənəsi yağları hazırlayırıq. N&ouml;vbəti ildə məhsullarımızın &ccedil;eşidlərini artırmağı, eyni zamanda itburnu və &uuml;z&uuml;m dənəsinin yağını &ccedil;ıxarmağı nəzərdə tutmuşuq.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Yarmarkada xalq sənətkarlarının, istedadlı qadınların və məhkumların da əl işlərinin sərgi-satışı təşkil olunub. Qadınların Nax&ccedil;ıvan daşlarından hazırladıqları bəzək əşyaları, qolbaqlar, qədim d&ouml;y&uuml;ş geyimi, eləcə də istehsal etdikləri m&uuml;xtəlif məhsullar diqqəti &ccedil;əkib.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sabirkənddən olan Xəlil Fərəcov deyib&nbsp;ki, 2012-ci ildən saldığım meyvə bağından hasilə gətirdiyim məhsulların satışını həyata ke&ccedil;irirəm. Bu il 2 hektardan artıq əlavə ərik bağı da salmışam. Gələcəkdə həmin bağın məhsullarının da yarmarkalarda satışını həyata ke&ccedil;irməyi d&uuml;ş&uuml;n&uuml;rəm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Babək rayonunun Nehrəm qəsəbəsindən olan Tofiq Abutalıbov deyib: &ldquo;Ailə təsərr&uuml;fatları həm də insanların məşğulluğunu təmin edir. Muxtar respublikada insanların rahatlığı &uuml;&ccedil;&uuml;n hər c&uuml;r şərait yaradılıb, dinamik inkişaf təmin edilib. B&uuml;t&uuml;n bunlar ulu &ouml;ndər Heydər Əliyev siyasətinin uğurla davam etdirilməsinin nəticəsidir. Biz də bu qayğının qarşılığında &ccedil;alışırıq ki, təsərr&uuml;fatımızı genişləndirək, məhsul bolluğu yaradaq&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Babək rayonunun Nehrəm qəsəbəsindən olan Cəmşid Həsənov deyib ki, əvvəllər istehsal etdiyimiz məhsulları necə və harada satacağımızı d&uuml;ş&uuml;n&uuml;rd&uuml;k. Lakin bu g&uuml;n yaradılan şəraitin nəticəsidir ki, məhsulumuzun &ouml;lkə ilə yanaşı, xaricdə də satışını həyata ke&ccedil;irir, &ccedil;oxlu sifarişlər qəbul edirik.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Babək rayonunun Naxışnərgiz kəndindən olan Qəzənfər Qurbanov deyib: &ldquo;Hər il məhsul &ccedil;eşidlərini artırır, onların satışını təşkil edərək maddi-rifah halımızı yaxşılaşdırırıq. Eyni zamanda insanların rahat yaşayışı &uuml;&ccedil;&uuml;n bir sıra tədbirlər g&ouml;r&uuml;l&uuml;r. Muxtar respublikada ən ucqar kəndlərdə belə abadlıq işləri həyata ke&ccedil;irilir, hər c&uuml;r infrastruktur yaradılır. Ke&ccedil;ən il i&ccedil;məli su təminatımız da yaxşılaşdırılmışdır. Buna g&ouml;rə də kənd sakinləri adından minnətdarlığımı bildirirəm&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ailə təsərr&uuml;fat&ccedil;ıları Anar Əzizov, Kamran Kazımov, Cəfər Muxtarov, Familə Mirzəyevə, H&uuml;sniyyə Bağırova, &Ccedil;i&ccedil;ək Bayramova və Balabəy Allahverdiyev də g&ouml;stərilən qayğı nəticəsində ailə təsərr&uuml;fatlarını xeyli genişləndirdiklərini bildirib, yaradılan şəraitə g&ouml;rə minnətdarlıq ediblər.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ali Məclisin Sədri ailə təsərr&uuml;fatlarının inkişafının m&uuml;h&uuml;m əhəmiyyətə malik olduğunu və g&ouml;stərilən qayğı nəticəsində &nbsp;&nbsp;&nbsp;istehsal olunan məhsulların &ccedil;eşidinin ilbəil artdığını bildirmiş, bundan sonra da m&uuml;vafiq d&ouml;vlət qurumları tərəfindən ailə təsərr&uuml;fatlarına dəstək g&ouml;stərilməsi ilə bağlı tapşırıqlar verib.</span></p>', 1, '2021-12-02 06:21:28', '2022-02-01 01:51:07'),
(33, 'Sığortalanmaq üçün nə etməli?', 29, 'news_1643634911.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsi bağlamaq istəyən şəxsin təqdim etdiyi məlumatların doğruluğu həmin g&uuml;n ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ı tərəfindən EKTİS-də olan məlumatlarla m&uuml;qayisə edilərək yoxlanılır.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong><em>Sığortalanmaq &uuml;&ccedil;&uuml;n nə etməli?</em></strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">EKTİS-də qeydiyyatdan ke&ccedil;dikdən sonra m&uuml;vafiq sığorta şirkətinə sığortalanmaqla bağlı m&uuml;raciət edilməlidir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta m&uuml;qaviləsi bağlamaq istəyən şəxsin təqdim etdiyi məlumatların doğruluğu həmin g&uuml;n ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ı tərəfindən EKTİS-də olan məlumatlarla m&uuml;qayisə edilərək yoxlanılır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı ona təqdim edildiyi sənədlər və məlumatlar əsasında m&uuml;qavilə bağlanması barədə qərarını Fondla razılaşdırmalıdır.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;qavilənin bağlanmasına razılıq verildiyi halda m&uuml;stəqil eskpert tərəfindən sığorta predmetinə baxış ke&ccedil;irilir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">İdarəedici qurumun məsul əməkdaşı m&uuml;stəqil ekspert tərəfindən g&ouml;ndərilmiş məlumat və sənədləri təhlil edərək sığorta m&uuml;qaviləsinin bağlanılıb-bağlanılmaması barədə qərar qəbul edir.</span></p>', 1, '2022-01-31 09:15:11', '2022-01-31 09:15:11'),
(34, 'Sığorta hadisəsi olsa nə etməli?', 29, 'news_1643634963.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta hadisələri barədə məlumat hadisənin baş vermə tarixindən 24 saat ərzində bildirilməlidir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta hadisələri barədə məlumatı hadisənin baş vermə tarixindən 24 saat ərzində vasitə&ccedil;i aqrar sığorta&ccedil;ıya və ya idarəedici quruma, eyni zamanda qanunvericiliyə uyğun olaraq, həmin hadisə barədə məlumatlandırılmalı olan digər səlahiyyətli d&ouml;vlət orqanlarına məlumat verməlidir.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Zərərin araşdırılması və qiymətləndirilməsi &uuml;&ccedil;&uuml;n m&uuml;stəqil ekspert cəlb olunur.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">M&uuml;stəqil ekspert tərəfindən təqdim olunan rəy və fotoşəkillər əsasında &ouml;dəniş və ya imtina edilir.</span></p>', 1, '2022-01-31 09:16:03', '2022-01-31 09:16:03'),
(35, 'Müstəqil ekspert kimdir?', 29, 'news_1643635026.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sığorta risklərini və (və ya) zərərləri qiymətləndirən, habelə sığorta hadisələrini araşdıran fiziki şəxs.</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\"><strong><em>M&uuml;stəqil ekspert olmaq &uuml;&ccedil;&uuml;n nə etməli?</em></strong></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Aqrar sığorta fəaliyyəti &uuml;zrə m&uuml;vafiq icra hakimiyyəti orqanının təşkil etdiyi&nbsp;<strong><a href=\"http://elacademy.az/qerarlar/aqrar-sigorta-fealiyyeti-uzre-musteqil-ekspertlerin-imtahaninin-kecirilmesi-qaydasi\">imtahandan</a></strong>&nbsp;m&uuml;vəffəqiyyətlə ke&ccedil;məli, m&uuml;vafiq sertifikat almalı və reyestrə daxil edilməlidir</span></p>', 1, '2022-01-31 09:17:06', '2022-01-31 09:17:06');

