INSERT INTO `static_pages` (`id`, `title`, `content`, `status`, `category`, `created_at`, `updated_at`) VALUES
(2, 'Kənd təsərrüfatının inkişafı diqqətdə saxlanılır.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin əməkdaşları tərəfindən avqust ayı ərzində kənd təsərr&uuml;fatının m&uuml;xtəlif sahələri ilə əlaqədar m&uuml;şavirələr, tarla seminarları ke&ccedil;irilib. Həm&ccedil;inin muxtar respublikada kartof yığımının qısa m&uuml;ddətdə başa &ccedil;atdırılması, məhsulun Nax&ccedil;ıvan Muxtar Respublikası İqtisadiyyat Nazirliyinin və sahibkarların soyuducu anbarlarına təhvil verilməsi ilə əlaqədar maarifləndirmə işləri aparılıb. Bi&ccedil;indən sonra təkrar əkinlərin həyata ke&ccedil;irilməsi, əkin sahələrində aqrotexniki qulluğun d&uuml;zg&uuml;n və vaxtında aparılması, payızlıq taxıl əkinlərinə hazırlıqla əlaqədar n&ouml;vbəli əkin sisteminə əməl etməklə sahələrin se&ccedil;ilməsi, keyfiyyətli şumun qaldırılması, toxumun dərmanlanmasının əhəmiyyəti m&ouml;vzularında torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı sahiblərinin iştirakı ilə tarla-seminar m&uuml;şavirələri ke&ccedil;irilib. &Ouml;tən ay nazirliyin əməkdaşları Ekologiya və Təbii Sərvətlər Nazirliyinin, Daşınmaz Əmlak və Torpaq Məsələləri &uuml;zrə D&ouml;vlət Xidmətinin m&uuml;təxəssisləri ilə birlikdə Ordubad və Culfa rayonları ərazisində torpaqların şorluluq dərəcəsi və eroziyası ilə bağlı monitorinqlər və planaalma işləri aparıblar. Ay ərzində Babək rayonundan &uuml;z&uuml;ml&uuml;klərdə və meyvə bağlarında məhsulun vəhşi arılardan qorunması ilə bağlı 16, Babək və Şərur rayonlarında təkrar əkinlərin aparılması ilə əlaqədar 19, payız calağının işlənməsi ilə bağlı 14, kartofun sadə &uuml;sullarla saxlanması ilə əlaqədar 21 nəfər nazirliyə m&uuml;raciət edib, m&uuml;təxəssislər tərəfindən onlara lazımi t&ouml;vsiyələr verilib. Hesabat d&ouml;vr&uuml;ndə Akademik Həsən Əliyev adına &ldquo;Araz&rdquo; Elmİstehsalat Birliyinin Aqrokimya, torpaqş&uuml;naslıq və torpaqların k&uuml;tləvi analizləri ş&ouml;bəsinin əməkdaşları tərəfindən Ordubad rayonunun &Uuml;st&uuml;p&uuml;, Darkənd və Aza yaşayış məntəqələrində torpaq tədqiqat işləri aparılıb, torpaq n&uuml;munələri g&ouml;t&uuml;r&uuml;lərək analiz olunması &uuml;&ccedil;&uuml;n laboratoriyaya verilib. &Uuml;&ccedil; torpaq m&uuml;lkiyyət&ccedil;isi və ailə təsərr&uuml;fatı baş&ccedil;ısı tərəfindən gətirilən 7 torpaq n&uuml;munəsi analiz olunaraq tərkibindəki qida maddələri barədə onlara məlumat verilib. Muxtar respublikada şoranlaşmış və eroziyaya uğramış torpaqların m&uuml;əyyənləşdirilməsi &uuml;&ccedil;&uuml;n tədqiqat işlərinə başlanılıb. Toxum&ccedil;uluq və bitkilərin m&uuml;hafizəsi idarəsinin m&uuml;təxəssisləri muxtar respublikanın rayonlarında ailə təsərr&uuml;fatı &uuml;zvləri, fermerlərlə kənd təsərr&uuml;fatı bitkilərinin başlıca xəstəlik və zərərvericiləri, onlara qarşı m&uuml;barizə tədbirləri barədə fərdi qaydada maarifləndirici s&ouml;hbətlər aparıblar. M&uuml;xtəlif əkin sahələrində xəstəlik və zərərvericilərin aşkarlanması məqsədilə idarəyə gətirilən n&uuml;munələrə baxılıb, 40 təsərr&uuml;fat sahibinə lazımi məsləhət və t&ouml;vsiyələr verilib. Toxum&ccedil;uluq təsərr&uuml;fatları və fermerlər tərəfindən laboratoriyaya gətirilən toxumlar analizdən ke&ccedil;irilib, analizin nəticəsi ilə bağlı onlara analiz aktı təqdim olunub. Payız-qış m&ouml;vs&uuml;m&uuml;ndə malqaranın saxlanması, arı ailələrinin qışlamaya hazırlanması, yarana biləcək xəstəliklərə qarşı profilaktik tədbirlərin aparılması məqsədilə sahibkarlarla fərdi qaydada maarifləndirici g&ouml;r&uuml;şlər ke&ccedil;irilib.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin mətbuat xidməti</strong></span></p>', 1, 18, '2021-11-29 02:19:59', '2021-11-29 03:53:36'),
(3, 'Naxçıvan Muxtar Respublikası Kənd Təsərrüfatı Nazirliyinə məktub yazmaq qaydaları', '<p>NAX&Ccedil;IVAN MUXTAR RESPUBLİKASI KƏND TƏSƏRR&Uuml;FATI NAZİRLİYİNİN &Uuml;NVANINA ELEKTRON FORMADA M&Uuml;RACİƏT G&Ouml;NDƏRMƏZDƏN ƏVVƏL, XAHİŞ EDİRİK QAYDALARLA TANIŞ OLUN:</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin rəsmi internet səhifəsi vasitəsi ilə elektron formada g&ouml;ndərilmiş m&uuml;raciətlər Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin məktublar ş&ouml;bəsinə daxil olur və &rdquo;Vətəndaşların m&uuml;raciətlərinə baxılması qaydası haqqında&rdquo; Azərbaycan Respublikasının 1997-ci il 10 iyun tarixli 314-IQ n&ouml;mrəli Qanununa, habelə Azərbaycan Respublikası Prezidentinin 1998-ci il 29 dekabr tarixli Fərmanı ilə təsdiq edilmiş &ldquo;D&ouml;vlət hakimiyyəti orqanlarında, idarə, təşkilat və m&uuml;əssisələrdə vətəndaşların təklif, ərizə və şikayətləri &uuml;zrə karg&uuml;zarlığın aparılması Qaydaları&rdquo;na uyğun olaraq qeydiyyata alınır və baxılır.</span></li>\r\n	<li><span style=\"font-size:16px\">Mətni yazmazdan əvvəl nəzərdə tutulmuş formanın d&uuml;zg&uuml;n cavablandırılması vacibdir.</span></li>\r\n	<li><span style=\"font-size:16px\">Mətn m&uuml;tləq yazıldığı dilin əlifbası ilə tərtib olunmalıdır, b&uuml;t&ouml;vl&uuml;kdə b&ouml;y&uuml;k və ya ki&ccedil;ik hərflərlə yazılmamalı, c&uuml;mlələrə b&ouml;l&uuml;nməli, durğu işarələrindən istifadə qaydalarına riayət edilməlidir.</span></li>\r\n	<li><span style=\"font-size:16px\">M&uuml;raciət aydın və səlis c&uuml;mlələrlə yazılmalıdır.</span></li>\r\n	<li><span style=\"font-size:16px\">M&uuml;raciət edərkən etika qaydaları g&ouml;zlənilməlidir. Nəzakətsiz ifadələr işlədildikdə və ya m&uuml;əllif &ouml;z&uuml; barədə məlumatları dəqiq g&ouml;stərmədikdə m&uuml;raciətə cavab verilmir.</span></li>\r\n	<li><span style=\"font-size:16px\">H&ouml;rmətli internet səhifəsi istifadə&ccedil;iləri!&nbsp;B&ouml;y&uuml;k informasiya axınının işlənməsi x&uuml;susiyyətlərini nəzərə alaraq, xahiş edirik bəzi məhdudiyyətlərə h&ouml;rmətlə yanaşasınız:</span>\r\n	<ul>\r\n		<li><span style=\"font-size:16px\">Bir mənbədən elektron məlumatın g&ouml;ndərilməsinin vaxtı serverin y&uuml;klənməsindən asılı olaraq 1-5 dəqiqə ilə məhdudlaşır.</span></li>\r\n		<li><span style=\"font-size:16px\">Elektron m&uuml;raciətin mətni 1500 işarədən &ccedil;ox olmamalıdır.</span></li>\r\n		<li><span style=\"font-size:16px\">Elektron m&uuml;raciətdə qoşma fayllar olmamalıdır. Mətni uzun olan məktublar, sənəd surətləri, fotoşəkillər və yol verilən digər əlavələr Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin E-mail:&nbsp;<strong><a href=\"mailto:ktn@nakhchivan.az\">ktn@nakhchivan.az</a></strong>&nbsp;&uuml;nvanına və ya adi po&ccedil;tla bu &uuml;nvana g&ouml;ndərilməlidir:</span></li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<address>Nax&ccedil;ıvan şəhəri, AZ 7000, Nizami k&uuml;&ccedil;əsi-30, Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi.</address>', 1, 15, '2021-11-29 02:58:18', '2021-11-29 03:10:02'),
(4, 'Naxçıvan Muxtar Respublikasının Kənd Təsərrüfatı  Nazirliyi haqqında Əsasnamənin təsdiq edilməsi barədə', '<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>NAX&Ccedil;IVAN MUXTAR RESPUBLİKASI ALİ MƏCLİSİ SƏDRİNİN</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>F Ə R M A N I</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin fəaliyyətini təmin etmək və strukturunu təkmilləşdirmək məqsədi ilə&nbsp;<strong>qərara alıram:</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikasının Kənd Təsərr&uuml;fatı Nazirliyi haqqında Əsasnamə təsdiq edilsin (əlavə olunur).</span></li>\r\n	<li><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikasının Nazirlər Kabineti:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px\">- Nax&ccedil;ıvan Muxtar Respublikasının Kənd Təsərr&uuml;fatı Nazirliyinin yeni strukturunu təsdiq etsin;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- bu Fərmandan irəli gələn məsələləri həll etsin.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:16px\">Bu Fərman imzalandığı g&uuml;ndən q&uuml;vvəyə minir.</span></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; Nax&ccedil;ıvan Muxtar Respublikası</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Ali Məclisinin Sədri&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; VASİF TALIBOV</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nax&ccedil;ıvan şəhəri, 26 fevral&nbsp; 2003-c&uuml; il</span></p>\r\n\r\n<p><span style=\"font-size:16px\">№&nbsp; 89-II FR</span></p>\r\n\r\n<hr />\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>&nbsp;Nax&ccedil;ıvan &nbsp;&nbsp;Muxtar &nbsp;&nbsp;Respublikası &nbsp;Ali&nbsp; Məclisi</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Sədrinin 2003-c&uuml; &nbsp;il &nbsp;26 fevral tarixli 89 -II FR</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>n&ouml;mrəli Fərmanı ilə təsdiq edilmişdir.</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikasının Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>Nazirliyi haqqında</strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>ƏSASNAMƏ</strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>I &Uuml;mumi m&uuml;ddəalar</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">1.1. Nax&ccedil;ıvan&nbsp;&nbsp;&nbsp; Muxtar Respublikasının Kənd Təsərr&uuml;fatı Nazirliyi (bundan sonra - Nazirlik) Nax&ccedil;ıvan Muxtar Respublikasında kənd təsərr&uuml;fatı sahəsində d&ouml;vlət siyasətini formalaşdıran və həyata ke&ccedil;irən mərkəzi icra hakimiyyəti orqanıdır.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">1.2. Nazirlik &ouml;z fəaliyyətində Azərbaycan Respublikasının və Nax&ccedil;ıvan Muxtar Respublikası Konstitusiyalarını, Azərbaycan Respublikasının və Nax&ccedil;ıvan Muxtar Respublikasının qanunlarını, Azərbaycan Respublikası Prezidentinin və Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin Fərman və sərəncamlarını, Azərbaycan Respublikası və Nax&ccedil;ıvan Muxtar Respublikası Nazirlər Kabinetinin qərar və sərəncamlarını, digər normativ h&uuml;quqi aktları və bu Əsasnaməni rəhbər tutur.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">1.3. Nazirlik &ouml;z fəaliyyətini digər mərkəzi və yerli icra hakimiyyəti orqanları, yerli &ouml;z&uuml;n&uuml; idarəetmə orqanları, habelə ictimai təşkilatlarla qarşılıqlı əlaqəli şəkildə həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">1.4. Nazirlik h&uuml;quqi şəxsdir, m&uuml;stəqil balansa, operativ idarə&ccedil;ilik h&uuml;ququ ilə d&ouml;vlət m&uuml;lkiyyətində olan əmlaka, &uuml;zərində D&ouml;vlət gerbinin təsviri və &ouml;z adı əks olunmuş m&ouml;h&uuml;rə, m&uuml;vafiq ştamplara və blanklara, bank hesablarına malikdir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">1.5. Nazirlik Nax&ccedil;ıvan şəhərində yerləşir.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>II Nazirliyin əsas vəzifələri</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nazirliyin əsas vəzifələri aşağıdakılardır:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.1. Kənd təsərr&uuml;fatı sahəsində d&ouml;vlət siyasətinin formalaşdırılmasının və həyata ke&ccedil;irilməsinin təmin edilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.2. Muxtar Respublikada aqrar islahatın həyata ke&ccedil;irilməsində yerli və rayon aqrar islahatı komissiyalarına əməli k&ouml;məklik g&ouml;stərilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.3. Kənd təsərr&uuml;fatı sahəsində sosial-iqtisadi inkişafın əsas istiqamətlərinin m&uuml;əyyənləşdirilməsi, kənd təsərr&uuml;fatı sahəsində m&uuml;vafiq standartlar, normalar və qaydaların işlənib hazırlanması.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.4. Təsərr&uuml;fatlarda toxum&ccedil;uluq istehsalı sahəsində y&uuml;ksək məhsuldarlığa və sabit irsi əlamətlərə malik, xəstəliklərə, ziyanvericilərə və m&uuml;xtəlif iqlim şəraitinə d&ouml;z&uuml;ml&uuml; bitki sortlarının yaradılması sahəsində seleksiya işlərinin aparılması, onların sort sınağının ke&ccedil;irilməsinin və d&ouml;vlət toxum ehtiyatlarının yaradılmasının təşkil edilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.5. Damazlıq işi sahəsində y&uuml;ksək məhsuldarlığa və sabit irsi əlamətlərə malik cins heyvanlar yetişdirilməsinin, onların&nbsp; sınağının, təkrar istehsalının və səmərəli istifadəsinin təşkil edilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.6. Kənd təsərr&uuml;fatının texnika, maşın, avadanlıq, ehtiyat hissələri və digər istehsal təyinatlı mallara olan tələbatının təhlil edilməsi və proqnozlaşdırılması, m&uuml;vafiq icra hakimiyyəti orqanları ilə birlikdə həmin tələbatın &ouml;dənilməsi &uuml;&ccedil;&uuml;n təkliflərin verilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.7. Kənd təsərr&uuml;fatı məhsulları istehsalının artırılmasına və emal sahələrinin inkişafına y&ouml;nəlmiş x&uuml;susi proqramların hazırlanması.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.8. Kənd təsərr&uuml;fatı m&uuml;əssisələrində torpağın m&uuml;nbitliyinin bərpasının, g&uuml;brə və digər kimyəvi preparatlardan d&uuml;zg&uuml;n və səmərəli istifadənin təmin edilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.9. Nazirlik sistemində m&uuml;hasibat u&ccedil;otu və statistik hesabat işlərinin təşkil edilməsi və təkmilləşdirilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.10. Nazirlik sistemində sosial-məişət və əmək şəraitinin yaxşılaşdırılması, əməyin m&uuml;hafizəsi, təhl&uuml;kəsizlik, istehsalat- sanitariya qaydalarına riayət edilməsinin təmin edilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.11. Bitki m&uuml;hafizəsi və bitki karantini sahəsində m&uuml;vafiq tədbirlərin həyata ke&ccedil;irilməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.12. Baytarlıq təbabəti sahəsində fəaliyyətin beynəlxalq tələblərə uyğun olaraq tənzimlənməsi.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">2.13. Qanunvericiliklə Nazirliyə həvalə edilmiş digər vəzifələrin həyata ke&ccedil;irilməsi</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>III Nazirliyin əsas funksiyaları</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nazirlik bu Əsasnamə ilə m&uuml;əyyən edilmiş vəzifələrə uyğun olaraq aşağıdakı əsas funksiyaları həyata ke&ccedil;irir:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.1. Tabeliyində olan idarə və təşkilatların fəaliyyətinə &ouml;z səlahiyyətləri &ccedil;ər&ccedil;ivəsində k&ouml;məklik g&ouml;stərir və nəzarət edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.2. Kənd təsərr&uuml;fatında y&uuml;ksək səmərəli ixtira və kəşflərin sınaq və tətbiqini təşkil edir. Kənd təsərr&uuml;fatı sahəsində qəbul olunmuş elmi-texniki proqramları həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.3. Kənd təsərr&uuml;fatı sisteminə daxil olan elmi təşkilatların inkişafına dair m&uuml;vafiq tədbirlər işləyib həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.4. M&uuml;əyyən edilmiş qaydada kənd təsərr&uuml;fatı sahəsində beynəlxalq elmi-texniki, iqtisadi əməkdaşlığın və əlaqələrin genişləndirilməsi sahəsində m&uuml;vafiq təkliflər hazırlayır və həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.5. Kənd təsərr&uuml;fatı məhsullarının istehsalının artırılması sahəsində texniki, texnoloji, təşkilati və iqtisadi tədbirləri işləyib hazırlayır və həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.6. &Ouml;z səlahiyyətləri h&uuml;dudlarında kənd təsərr&uuml;fatında təsərr&uuml;fat fəaliyyətinin h&uuml;quqi tənzimlənməsini həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.7. Suvarılan torpaqların və kənd təsərr&uuml;fatının su təchizatının yaxşılaşdırımasına dair təkliflər verir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.8. Kəndli (fermer) təsərr&uuml;fatlarına və digər kənd təsərr&uuml;fatı əmtəə istehsal&ccedil;ılarına informasiya-məsləhət xidməti təşkil edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.9. Muxtar Respublika ərazisində m&ouml;vcud qanunvericiliyə uyğun fiziki və h&uuml;quqi şəxslərə məxsus olan kənd təsərr&uuml;fatı maşın və mexanizmlərinin texniki vəziyyəti və istismarı &uuml;zərində d&ouml;vlət nəzarətini həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.10. M&uuml;əyyən edilmiş qaydada sahibkarlıq fəaliyyətinin n&ouml;vləri ilə məşğul olmaq &uuml;&ccedil;&uuml;n fiziki və h&uuml;quqi şəxslərə m&uuml;vafiq k&ouml;məklik g&ouml;stərir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.11. D&ouml;vlət bitki m&uuml;hafizə və bitki karantini xidmətlərini təşkil edir və d&ouml;vlət fitosanitar nəzarətini həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.12. Bitki m&uuml;hafizə vasitələrinin və aqrokimyəvi maddələrin sınağının və qeydiyyatının təşkilində iştirak edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.13. &Ouml;z səlahiyyətləri daxilində kənd təsərr&uuml;fatı bitkiləri toxum&ccedil;uluğu sahəsində d&ouml;vlət toxum&ccedil;uluq xidmətini təşkil edir və həmin sahəyə d&ouml;vlət nəzarətini həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.14. D&ouml;vlət və sığorta toxum fondlarına sifariş verir və bu fondlara toxum tədar&uuml;k&uuml; məsələlərini həll edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.15. &Ouml;z səlahiyyətləri daxilində pestisidlərin və aqrokimyəvi maddələrin saxlanması və tətbiqi &uuml;zərində, habelə kənd təsərr&uuml;fatı məhsullarında, yemlərdə, torpaqda zəhərli kimyəvi maddələrin qalıq miqdarına d&ouml;vlət nəzarətini təşkil edir və həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.16. Pestisidlərin və aqrokimyəvi maddələrin qeydiyyatını həyata ke&ccedil;irir, bu məqsədlər &uuml;&ccedil;&uuml;n m&uuml;vafiq komissiya yaradır.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.17. Kənd təsərr&uuml;fatı sahəsində m&uuml;asir tələblərə uyğun inkişaf strategiyasının və investisiya proqramlarının hazırlanmasında fəal iştirak edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.18. Kənd təsərr&uuml;fatı sahələrinə investisiyaları, o c&uuml;mlədən xarici investisiyaları cəlb etmək &uuml;&ccedil;&uuml;n məqsədy&ouml;nl&uuml; iş aparır.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.19. Bazar iqtisadiyyatının tələblərinə uyğun olaraq kənd təsərr&uuml;fatında təhsil, kadr hazırlığı sisteminin k&ouml;kl&uuml; surətdə dəyişdirilməsi və bu sahədə beynəlxalq əməkdaşlıq &ccedil;ər&ccedil;ivəsində tədbirlər g&ouml;r&uuml;lməsində iştirak edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.20. Vətəndaşların ərizə, şikayət və təkliflərinə m&ouml;vcud qanunvericiliyə uyğun baxılmasını təşkil edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.21. Baytarlıq xidmətini təşkil edir, d&ouml;vlət baytarlıq nəzarətini həyata ke&ccedil;irir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.22. &Ouml;z səlahiyyətləri daxilində baytarlıq preparatlarının, vitaminlərin, yem əlavələrinin, habelə mal-qaranın boy artımını surətləndirən və məhsuldarlığı y&uuml;ksəldən bioloji stimulyatorların və hormonların saxlanmasına, heyvandarlıqda və baytarlıqda tətbiqinə nəzarət edir, onların sınağını və qeydiyyatını aparır.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">3.23. Qanunvericiliklə Nazirliyə həvalə edilmiş vəzifələrin həyata ke&ccedil;irilməsi &uuml;&ccedil;&uuml;n digər funksiyaları həyata ke&ccedil;irir.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>IV Nazirliyin h&uuml;quqları</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nazirlik &ouml;z vəzifələrini və funksiyalarını həyata ke&ccedil;irmək &uuml;&ccedil;&uuml;n aşağıdakı h&uuml;quqlara malikdir:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.1. M&uuml;əyyən edilmiş qaydada D&ouml;vlət Statistika Komitəsindən, digər mərkəzi və yerli orqanlardan kənd təsərr&uuml;fatı ilə əlaqədar məsələlərə dair lazım olan məlumatları almaq və təhlil etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.2. M&uuml;vafiq mərkəzi və yerli icra hakimiyyəti orqanları ilə birlikdə kəndin sosial və iqtisadi inkişafı, kənd əhalisinin məşğulluğu və sosial m&uuml;dafiəsinin yaxşılaşdırılması &uuml;zrə d&ouml;vlət proqramlarının və tədbirlərinin işlənib hazırlanmasında və həyata ke&ccedil;irilməsində iştirak etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.3. M&uuml;əyyən edilmiş qaydada tabeliyində olan m&uuml;əssisə və idarələrin fəaliyyətini tənzimləyən əsasnamələri təsdiq etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.4. &Ouml;z səlahiyyətləri &ccedil;ər&ccedil;ivəsində tabeliyində olan d&ouml;vlət m&uuml;əssisə və idarələrinə əmr və g&ouml;stərişlər vermək, onların d&uuml;zg&uuml;n və vaxtında icra olunmasına nəzarət etmək, həmin m&uuml;əssisə və idarələr tərəfindən qanunvericiliyə zidd qəbul edilmiş qərarların icrasını dayandırmaq və ya ləğv etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.5. Nazirliyin səlahiyyətinə aid olan məsələlərə dair m&uuml;əyyən edilmiş qaydada konfrans, m&uuml;şavirə, seminarlar və sərgi-satış təşkil etmək, xarici &ouml;lkələrdə ke&ccedil;irilən bu c&uuml;r tədbirlərdə iştirak etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.6. Kənd təsərr&uuml;fatı m&uuml;təxəssislərinə və kadrlarına olan tələbatı m&uuml;əyyən etmək, onların hazırlanmasına və təkmilləşdirilməsinə dair təkliflər vermək, kənd təsərr&uuml;fatı &uuml;zrə tədris planlarının və proqramlarının hazırlanmasında iştirak etmək.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">4.7. Qanunvericiliklə nəzərdə tutulmuş digər h&uuml;quqları həyata ke&ccedil;irmək.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><strong>V Nazirliyin idarə olunması</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.1. Kənd Təsərr&uuml;fatı Nazirliyinə Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin Fərmanı ilə vəzifəyə təyin edilən nazir rəhbərlik edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.2. Kənd Təsərr&uuml;fatı nazirinin Nax&ccedil;ıvan Muxtar Respublikası Ali Məclisi Sədrinin Sərəncamı ilə vəzifəyə təyin olunan bir m&uuml;avini vardır.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.3. Nazirliyin mərkəzi aparatının strukturu və iş&ccedil;ilərinin sayı Nax&ccedil;ıvan Muxtar Respublikası Nazirlər Kabineti tərəfindən təsdiq olunur.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.4. Nazir &ouml;z səlahiyyətləri daxilində:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Nazirliyin fəaliyyətinə rəhbərliyi həyata ke&ccedil;irir, Nazirliyə həvalə olunmuş vəzifə və funksiyaların yerinə yetirilməsinə şəxsi məsuliyyət daşıyır, m&uuml;avini və Nazirliyin mərkəzi aparatının struktur b&ouml;lmələrinin rəhbərləri arasında səlahiyyət b&ouml;lg&uuml;s&uuml; aparır;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- əmrlər, sərəncamlar, təlimatlar və g&ouml;stərişlər verir;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- m&uuml;əyyən olunmuş qaydada Nazirliyin mərkəzi aparatının iş&ccedil;ilərini və Nazirliyin nəzdindəki d&ouml;vlət kənd təsərr&uuml;fatı xidmətlərinin rəhbərlərini &ouml;z əmri ilə vəzifəyə təyin edir və vəzifədən azad edir;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Nazirliyin mərkəzi aparatının struktur b&ouml;lmələrinin, tabeliyində olan idarə, m&uuml;əssisə və təşkilatların əsasnamələrini və təlimatlarını habelə Elmi-Texniki Şuranın heyətini və Əsasnaməsini təsdiq edir;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Nazirlikdə nəzarət-təftiş işini, ilkin u&ccedil;ot və hesabatı təşkil edir, tabeliyindəki idarə, m&uuml;əssisə və təşkilatların hesabat balanslarını təsdiq edir, m&uuml;hasibat u&ccedil;otu və hesabat işlərinin d&uuml;zg&uuml;n aparılmasına nəzarət edir;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- q&uuml;vvədə olan normativ h&uuml;quqi sənədlərə m&uuml;vafiq olaraq nazirlikdə karg&uuml;zarlığın operativ və d&uuml;zg&uuml;n aparılmasını, həm&ccedil;inin işdə d&ouml;vlət sirrinin saxlanmasını təmin edir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.5. Nazirlik kollegiyallıq və vahid rəhbərlik prinsiplərinin əsasında və qanunvericiliyə uyğun olaraq idarə edilir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.6. Kənd Təsərr&uuml;fatı Nazirliyində kollegiya yaradılır. Kollegiyanın tərkibinə nazir (kollegiyanın sədri), m&uuml;avini, vəzifəyə g&ouml;rə nazirliyin digər məsul vəzifəli iş&ccedil;iləri daxildir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.7. Kollegiyanın say tərkibi və &uuml;zvləri nazirin təqdimatı əsasında Nax&ccedil;ıvan Muxtar Respublikası Nazirlər Kabineti tərəfindən təsdiq edilir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.8. Kollegiya iclasının qərarları sadə səs &ccedil;oxluğu olduqda qəbul edilir və bir qayda olaraq nazirin əmri ilə rəsmiləşdirilir və həyata ke&ccedil;irilir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.9. Kollegiyanın sədri və onun &uuml;zvləri arasında fikir ayrılığı olduqda sədr &ouml;z qərarını həyata ke&ccedil;irir və bu barədə Nax&ccedil;ıvan Muxtar Respublikası Nazirlər Kabinetinə məlumat verir. Kollegiyanın &uuml;zvləri də &ouml;z n&ouml;vbəsində Nazirlər Kabinetinə m&uuml;raciət etmək h&uuml;ququna malikdirlər.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.10. Kollegiyanın iclaslarında Nazirliyin kənd təsərr&uuml;fatı sahəsində inkişaf proqramı, tabeliyində olan d&ouml;vlət m&uuml;əssisə, idarə və təşkilatların əsas fəaliyyəti, əsas fondlardan, materiallar, maliyyə və əmək ehtiyatlarından istifadənin vəziyyəti, elmi-texniki nailiyyətlərin, m&uuml;tərəqqi texnologiyaların tətbiq edilməsi və digər problemlər m&uuml;zakirə edilir və onlar barədə qərar qəbul edilir.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.11. Zəruri hallarda kollegiyanın iclaslarında iştirak etmək &uuml;&ccedil;&uuml;n m&uuml;zakirə olunan məsələlərlə bilavasitə əlaqəli olan icra hakimiyyəti orqanlarının, idarə, m&uuml;əssisə və təşkilatların, habelə ictimai təşkilatların və yerli &ouml;z&uuml;n&uuml;idarəetmə orqanlarının n&uuml;mayəndələri dəvət oluna bilər.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">5.12. Kənd təsərr&uuml;fatı sahəsində d&ouml;vlət siyasəti məsələlərinin, kənd təsərr&uuml;fatı elminin əsas istiqamətlərinin, elmi araşdırmaların və qabaqcıl xarici &ouml;lkələrin son nailiyyətlərinin &uuml;mumiləşdirilməsi, &ouml;yrənilməsi, texnoloji layihələrin ekspertizasının təşkil edilməsi və onların tətbiqi barədə rəy vermək məqsədi ilə Azərbaycan Milli Elmlər Akademiyası Nax&ccedil;ıvan B&ouml;lməsinin, digər elmi idarələrin alim və m&uuml;təxəssislərini cəlb etməklə Nazirlilkdə Elmi-Texniki Şura yaradılır, habelə qanunvericiliklə m&uuml;əyyən edilmiş qaydada Nazirliyin səlahiyyətlərinə aid edilmiş məsələlərə dair toplular, x&uuml;susi informasiya b&uuml;lletenləri, vərəqələr və digər mətbu nəşrlər buraxılır.&nbsp;</span></p>', 1, 16, '2021-11-29 03:15:20', '2021-11-29 03:17:07'),
(5, 'Fermerlərə məsləhət', '<p><span style=\"font-size:18px\">Burada tezliklə məlumat yerləşdiriləcək.</span></p>', 1, 17, '2021-11-29 03:18:21', '2021-11-29 03:18:21'),
(6, 'Aqrar sahədə maarifləndirmə işləri davam etdirilir.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi cari ilin iyul ayında da fəaliyyətini nazirliyin illik iş planının və D&ouml;vlət proqramlarının icrası istiqamətində həyata ke&ccedil;irmişdir. &Ouml;tən ay nazirlik əməkdaşları tərəfindən yaz işlərinin optimal m&uuml;ddətdə aparılması, vegetasiya m&uuml;ddətində aqrotexniki tədbirlərdən d&uuml;zg&uuml;n istifadə edilməsi, bi&ccedil;in başa &ccedil;atdıqdan sonra sahələrdən k&uuml;ləşin &ccedil;ıxarılması və keyfiyyətli şum aparılaraq təkrar əkinlərə, həm&ccedil;inin payız əkinlərinə hazırlıq işlərinin g&ouml;r&uuml;lməsi, tərəvəz sahələrindən və meyvə bağlarından yığılan məhsulların soyuducu anbarlarda saxlanılması, eləcə də şənbə-bazar g&uuml;nləri ke&ccedil;irilən kənd təsərr&uuml;fatı yarmarkasına &ccedil;ıxarılması barədə sahibkarlar və ailə təsərr&uuml;fatı &uuml;zvləri ilə maarifləndirici s&ouml;hbətlər aparılmışdır. Ay ərzində Babək, Culfa və Şərur rayonlarından olan təsərr&uuml;fat sahibləri &uuml;z&uuml;ml&uuml;klərdə, meyvə bağlarında həyata ke&ccedil;irilən kompleks tədbirlər və təkrar əkinlərin əlverişli &uuml;sullarla aparılması ilə bağlı nazirliyə m&uuml;raciət etmişdir. M&uuml;raciət olunan məsələlərlə bağlı lazımi t&ouml;vsiyələr verilmiş, əməli k&ouml;məkliklər g&ouml;stərilmişdir. Hesabat d&ouml;vr&uuml;ndə &ldquo;Araz&rdquo; Elm-İstehsalat Birliyində Elmişuranın iclası ke&ccedil;irilmiş, tədqiqat və təsərr&uuml;fat işləri m&uuml;zakirə olunmuş, m&uuml;vafiq tapşırıqlar verilmişdir. BirliyinAqrokimya, torpaqş&uuml;naslıq və torpaqların k&uuml;tləvi analizləri ş&ouml;bəsinin əməkdaşları tərəfindən torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı baş&ccedil;ıları tərəfindən gətirilən torpaq n&uuml;munələri analiz olunaraq tərkibindəki qida maddələri barədə onlara məlumat verilmişdir. Taxıl bitkilərinin seleksiyası, bitki m&uuml;hafizəsi və toxum&ccedil;uluq ş&ouml;bəsi tərəfindən payızlıq buğda sortu &uuml;zərində tədqiqat,seleksiya və se&ccedil;mə işləri sona &ccedil;atmışdır. Sahələrdə aprobasiya aparılmış, n&uuml;munə dərzləri g&ouml;t&uuml;r&uuml;lm&uuml;şd&uuml;r. Hazırda məhsuldarlıq g&ouml;stəriciləri araşdırılır. Toxum&ccedil;uluq və Bitkilərin M&uuml;hafizəsi İdarəsinə iyul ayında 47 ayrı-ayrı ailə təsərr&uuml;fat&ccedil;ıları, fermerlər tərəfindən meyvə bağı əkinlərində xəstəlik və zərərvericilərin aşkarlanması məqsədilə gətirilən n&uuml;munələrə baxılmış, həm&ccedil;inin xəstəlik və zərərvericilərin aşkarlanması məqsədilə idarənin m&uuml;təxəssisləri tərəfindən həmin sahələrə baxış ke&ccedil;irilmiş, tələb olunan m&uuml;barizə tədbirləri haqqında sahibkarlara məsləhətlər verilmişdir. Yay otlaqlarından səmərəli istifadə, qoyunlara zootexniki qaydalara uyğun qulluq edilməsi, mal-qara arasında baytarlıq tədbirlərinin vaxtında aparılması və yovşan bitkisinin əhəmiyyəti haqqında sahibkarlarla fərdi qaydada maarifləndirici g&ouml;r&uuml;şlər ke&ccedil;irilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin mətbuat xidməti</strong></span></p>', 1, 18, '2021-11-29 03:22:29', '2021-11-29 03:56:09'),
(7, 'Aqrar sahədə işlər davam etdirilir.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi 2020-ci ilin iyun ayında da fəaliyyətini nazirliyin illik iş planının və D&ouml;vlət proqramlarının icrası istiqamətində həyata ke&ccedil;irmişdir. İyun ayında taxıl bi&ccedil;ininin başlaması ilə əlaqədar nazirlikdə yaradılmış operativ qərargah vasitəsilə bi&ccedil;inin gedişinə nəzarət edilir. Nazirlik əməkdaşları tərəfindən muxtar respublikada taxıl bi&ccedil;ininin qısa m&uuml;ddətdə başa &ccedil;atdırılması, bi&ccedil;in zamanı baş verə biləcək fəsadların tez bir vaxtda aradan qaldırılması barədə sahibkarlara və ailə təsərr&uuml;fatı &uuml;zvlərinə maarifləndirici məsləhətlər verilmişdir. &Ouml;tən ay meyvə və &uuml;z&uuml;m bağlarında, bostan-tərəvəz sahələrində xəstəlik və zərərvericilərə qarşı m&uuml;barizə tədbirlərinin aparılması istiqamətində maarifləndirmə tədbirləri davametdirilmişdir. &Uuml;mumilikdə, muxtar respublika &uuml;zrə 475 hektar yonca sahəsində fitanomus zərərvericisinə, 966 hektar taxıl, həm&ccedil;inin cavan meyvə bağlarında si&ccedil;an və si&ccedil;anabənzər gəmiricilərə, 66 hektar kartof sahəsində Kolorado kartof b&ouml;cəyinə, 7 min 321 hektar taxıl və 191 hektar tərəvəz sahəsində alaq otlarına, 136 hektar bostan və tərəvəz sahələrində, eləcə də 27 hektar sahəni əhatə edən bəzək bitkilərinin zərərverici və xəstəliklərinə qarşı kimyəvi m&uuml;barizə tədbirlərinin əhəmiyyəti təsərr&uuml;fat sahiblərinə bildirilmişdir. Həm&ccedil;inin Babək rayonundan 6, Culfa rayonundan 2 təsərr&uuml;fat sahibinin m&uuml;raciəti əsasında &uuml;z&uuml;ml&uuml;klərində və meyvə bağlarında həyata ke&ccedil;irilən kompleks tədbirlərlə bağlı k&ouml;məkliklər g&ouml;stərilmiş, Babək və Şərurrayonlarından nazirliyəm&uuml;raciət edən 12 təsərr&uuml;fatsahibinə tərəvəz əkinlərində m&uuml;tərəqqi &uuml;sulların tətbiqinə dair lazımi t&ouml;vsiyələr verilmişdir. &ldquo;Araz&rdquo; Elm-İstehsalat Birliyinin Aqrokimya, torpaqş&uuml;naslıq və torpaqların k&uuml;tləvi analizləri ş&ouml;bəsinin əməkdaşları tərəfindən torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı baş&ccedil;ıları tərəfindən gətirilən torpaq n&uuml;munələri analiz olunmuş, nəticələrə uyğun g&uuml;brə normaları hesablanaraq yazılı məlumatlar verilmişdir. Nax&ccedil;ıvan Muxtar Respublikası İqtisadiyyat Nazirliyinin &ldquo;İstehlak Mallarının Ekspertizası Mərkəzi&rdquo; Məhdud Məsuliyyətli Cəmiyyəti tərəfindən gətirilmiş 2 &ccedil;eşiddə 6 halda kompeze və azot g&uuml;brələri analiz olunaraq nəticələri haqqında məlumatlar aidiyyəti &uuml;zrə g&ouml;ndərilmişdir. Toxum&ccedil;uluq və Bitkilərin M&uuml;hafizəsi İdarəsinin m&uuml;təxəssisləri muxtar respublikanın m&uuml;xtəlif ərazilərində m&uuml;şahidələr aparmış, xəstəlik və zərərvericilərlə sirayətlənmiş sahələr m&uuml;əyyən edilmişdir. Heyvandarlıqda mal-qaranın inkişaf etdirilməsi, onlara zoobaytar qaydalara uyğun qulluq edilməsi və cins tərkibinin yaxşılaşdırılması istiqamətində maarifləndirmə tədbirləri davam etdirilmiş, arı ailələrinin mərhələli şəkildə k&ouml;&ccedil;&uuml;r&uuml;lməsi və xəstəliklərinə qarşı profilaktik tədbirlərin aparılması məqsədilə arı&ccedil;ılarla g&ouml;r&uuml;şlər ke&ccedil;irilmişdir.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin mətbuat xidməti</strong></span></p>', 1, 18, '2021-11-29 03:22:47', '2021-11-29 03:57:47'),
(8, 'Aqrar sahənin inkişafı diqqətdə saxlanılır.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">2020-ci ilin may ayında Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi və struktur b&ouml;lmələrinin m&uuml;təxəssisləri kənd təsərr&uuml;fatının b&uuml;t&uuml;n sahələrində y&uuml;ksək məhsuldarlığın əldə olunması &uuml;&ccedil;&uuml;n maarifləndirici m&uuml;şavirələr, tarla seminarları ke&ccedil;irmiş, təsərr&uuml;fat sahibləri ilə fərdi qaydada s&ouml;hbətlər aparmışlar. Nazirlik əməkdaşları tərəfindən muxtar respublikada ke&ccedil;irilən seminar-m&uuml;şavirə və fərdi g&ouml;r&uuml;şlərdə yaz işlərinin əkin atlaslarına uyğun optimal m&uuml;ddətdə aparılması, bostan-tərəvəz əkini zamanı, əsasən, yerli və yerli şəraitə uyğunlaşan məhsuldar toxumlaXXrdan istifadə edilməsi t&ouml;vsiyə olunmuşdur. Əkinlərdə xəstəlik və zərərvericilərin qarşısının alınmasında aqrotexniki m&uuml;barizənin tətbiq olunmasına &uuml;st&uuml;nl&uuml;k verilməsi qeyd edilmişdir. &Uuml;mumilikdə, muxtar respublika &uuml;zrə 475 hektar yonca sahəsində fitonomus zərərvericisinə, 966 hektar taxıl və cavan meyvə bağlarında si&ccedil;an və si&ccedil;anabənzər gəmiricilərə, 66 hektar kartof sahəsində kolorado kartof b&ouml;cəyinə, 7321 hektar taxıl sahəsində alaq otlarına, eləcə də 16 hektarsahəni əhatə edən bəzək bitkilərinin zərərverici və xəstəliklərinə qarşı kimyəvi m&uuml;barizə aparılmışdır. Taxıl bi&ccedil;ini kampaniyasına hazırlıqla bağlı Nax&ccedil;ıvan Muxtar Respublikası F&ouml;vqəladə Hallar Nazirliyi və &ldquo;Nax&ccedil;ıvan Aqrolizinq&rdquo; A&ccedil;ıq Səhmdar Cəmiyyətinin n&uuml;mayəndələri ilə birlikdə muxtar respublikanın rayonlarında taxılbi&ccedil;ən kombayn sahibkarları ilə fərdi qaydada s&ouml;hbətlər aparılmış, bi&ccedil;ində iştirak edəcək kombaynların texniki vəziyyəti və sayı m&uuml;əyyən edilmişdir. &ldquo;Araz&rdquo; Elm-İstehsalat Birliyinin Aqrokimya, torpaqş&uuml;naslıq və torpaqların k&uuml;tləvi analizləri ş&ouml;bəsinin əməkdaşları tərəfindən torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı baş&ccedil;ılarının gətirdikləri torpaq n&uuml;munələri analiz olunaraq tərkibindəki qida maddələrinin miqdarı barədə məlumatların verilməsi işləri davam etdirilmişdir. Toxum&ccedil;uluq və bitkilərin m&uuml;hafizəsi idarəsinə m&uuml;raciət edən 91 ailə təsərr&uuml;fatı baş&ccedil;ısı və fermerlərin istixana təsərr&uuml;fatlarına, meyvə və &uuml;z&uuml;m bağlarına baxış ke&ccedil;irilmiş, tələb olunan m&uuml;barizə tədbirləri haqqında məsləhət və t&ouml;vsiyələr verilmişdir. Toxum&ccedil;uluq təsərr&uuml;fatları və fermerlər tərəfindən laboratoriyaya gətirilən toxumlar analizdən ke&ccedil;irilmiş və nəticələrlə bağlı analiz aktı təqdim olunmuşdur. &ldquo;2017-2022-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı &uuml;zrə D&ouml;vlət Proqramı&rdquo;nın və &ldquo;Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı ilə bağlı tədbirlər planı&rdquo;nın m&uuml;vafiq bəndlərinin icrası ilə əlaqədar maarifləndirici tədbirlər həyata ke&ccedil;irilmişdir. &ldquo;Nax&ccedil;ıvan Muxtar Respublikasında damazlıq heyvandarlığın inkişafının əsasları&rdquo; adlı kitab və b&uuml;t&uuml;n rayonların yaşayış məntəqələri &uuml;zrə &ldquo;Heyvan cinslərinin rayonlaşdırılmasının atlası&rdquo; hazırlanmışdır.</span></p>', 1, 18, '2021-11-29 03:24:40', '2021-11-29 03:58:38'),
(9, 'Aqrar sahədə işlər davam etdirilib.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi 2020-ci ilin aprel ayında &ouml;z fəaliyyətini nazirliyin illik iş planının və D&ouml;vlət Proqramlarının icrası istiqamətində həyata ke&ccedil;irilib. Nazirlik əməkdaşları tərəfindən muxtar respublikada payızlıq taxıl əkinlərinə 2-ci yemləmə g&uuml;brələrinin verilməsi, taxıl sahələrində alaq otlarına, eyni zamanda sarı &ccedil;i&ccedil;əyə qarşı m&uuml;barizə tədbirlərinin aparılması barədə sahibkarlara və ailə təsərr&uuml;fatı baş&ccedil;ılarına maarifləndirici məsləhətlər verilib.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kartof əkinlərinin əkin atlaslarına uyğun aqrotexniki qaydada aparılmasının əhəmiyyəti, meyvə və &uuml;z&uuml;m &nbsp;bağlarında zərərverici və xəstəliklərə qarşı qabaqlayıcı m&uuml;barizə tədbirlərinin həyata ke&ccedil;irilməsinin məhsuldarlığa təsiri haqqında torpaq m&uuml;lkiyyət&ccedil;ilərinə məsləhətlər verilib.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">2020-ci&nbsp; təsərr&uuml;fat ilində Azərbaycan Respublikası Kənd Təsərr&uuml;fatı Nazirliyinin D&ouml;vlət Fitosanitar Nəzarəti Xidmətinin Bitki M&uuml;hafizə Mərkəzindən kənd təsərr&uuml;fatı bitkilərinin sirayətlənə biləcəyi karantin mənşəli zərərverici və alaq otlarına qarşı m&uuml;barizə tədbirlərinin aparılması &uuml;&ccedil;&uuml;n pestisid alınıb gətirilmiş, norma və nisbətləri nəzərə alınaraq muxtar respublikanın Rayon İcra Hakimiyyətlərinə paylanılıb.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&Uuml;mumilikdə muxtar respublikada yazın əvvəlindən 235 hektar yonca sahəsində fitanomus zərərvericisinə, 4675 hektar taxıl və cavan meyvə bağlarında si&ccedil;an və si&ccedil;anabənzər gəmiricilərə qarşı,&nbsp; 3761 hektar taxıl sahəsində alaq otlarına qarşı, həm&ccedil;inin&nbsp; 8 hektar sahədə bəzək bitkilərinin zərərverici və xəstəliklərinə qarşı kimyəvi m&uuml;barizə tədbirləri aparılıb.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ay ərzində Culfa rayonunda 2, Babək rayonunda 6 &uuml;z&uuml;m və meyvə bağlarında kompleks tədbirlərin aparılması ilə bağlı k&ouml;məklik g&ouml;stərilib.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&ldquo;Araz&rdquo; Elm İstehsalat Birliyinin Aqrokimya, Torpaqş&uuml;naslıq və Torpaqların K&uuml;tləvi Analizləri ş&ouml;bəsinin əməkdaşları tərəfindən torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı baş&ccedil;ıları tərəfindən gətirilmiş torpaq n&uuml;munələri analiz olunmuş və tərkibindəki qida maddələrinin miqdarı barədə aidiyyatına məlumat təqdim edilib.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Toxum&ccedil;uluq və bitkilərin m&uuml;hafizəsi idarəsinə&nbsp;85 nəfər ayrı-ayrı ailə təsərr&uuml;fatı sahibləri və fermerlər tərəfindən istixana, meyvə, &uuml;z&uuml;m bağı əkinlərində xəstəlik və zərərvericilərin aşkarlanması barədə m&uuml;raciətlər olunmuş, m&uuml;təxəssislər tərəfindən həmin sahələrə baxış ke&ccedil;irilmiş və tələb olunan m&uuml;barizə tədbiri haqqında sahibkarlara məsləhət və t&ouml;vsiyyələr veriblər.Toxum&ccedil;uluq təsərr&uuml;fatları və fermerlər tərəfindən laboratoriyaya gətirilən toxumlar analizdən ke&ccedil;irilərək nəticəsi haqda onlara analiz aktı verilib.Muxtar Respublikanın rayonlarında&nbsp; mineral və &uuml;zvi g&uuml;brələrin verilməsində, taxıl sahələrində alaq otlarına qarşı m&uuml;barizə tədbirlərinin aparılmasında,&nbsp; yaz əkinlərinin ke&ccedil;irilməsində m&uuml;assir texnikalardan istifadə edilməsinin əhəmiyyəti haqqında mexanizator və fermerlərlə fərdi qaydada s&ouml;hbətlər&nbsp; aparılmış, fermerlərə və mexanizatorlara yeni m&uuml;assir texnikaların lizinqə g&ouml;t&uuml;r&uuml;lməsinin şərtləri onların diqqətinə &ccedil;ardırılıb.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Qoyun&ccedil;uluq &nbsp;təsərr&uuml;fatlarında yeni doğulan balalara zoobaytar qaydalarına uyğun qulluq edilməsinin əhəmiyyəti, mal-qaranın cins tərkibinin yaxşılaşdırılması &uuml;&ccedil;&uuml;n suni mayalandırma tədbirlərinin davam etdirilməsi və baytarlıq tədbirlərinin aparılması ilə&nbsp; bağlı&nbsp; sahibkarlarla maarifləndirici s&ouml;hbətlər aparılıb.&ldquo;2017-2022-ci illərdə Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı &uuml;zrə&nbsp; D&ouml;vlət Proqramı&rdquo;nın&nbsp; və &ldquo;Nax&ccedil;ıvan Muxtar Respublikasında arı&ccedil;ılığın inkişafı ilə bağlı&nbsp; tədbirlər planı&rdquo;nınm&uuml;vafiq bəndlərinin icrası diqqətdə saxlanılıb.</span></p>', 1, 18, '2021-11-29 03:24:57', '2021-11-29 04:00:01'),
(10, 'Yaz-tarla işlərinə hazırlıq davam etdirilir.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi 2020-ci ilin fevral ayında &ouml;z fəaliyyətini illik iş planının və D&ouml;vlət proqramlarının icrası istiqamətində həyata ke&ccedil;irmişdir. Belə ki, nazirliyin m&uuml;təxəssisləri tərəfindən torpaq m&uuml;lkiyyət&ccedil;iləri və ailə təsərr&uuml;fatı baş&ccedil;ılarının iştirakı ilə payızlıq taxıl sahələrinə mineral g&uuml;brələrin iki mərhələdə verilməsinin əhəmiyyəti, kartofsahələrinin əkinə hazırlanması, keyfiyyətli toxum materiallarından istifadə etməklə y&uuml;ksək məhsuldarlığa nail olunması, toxumun dərmanlanması, əkindən qabaq torpaq analizinin aparılması m&ouml;vzularında seminarm&uuml;şavirələr ke&ccedil;irilmişdir. Taxıl, meyvə və &uuml;z&uuml;m bağlarında zərərvericilərə qarşı kompleks m&uuml;barizə tədbirlərinin aparılması qaydaları, yaztarla işlərinə hazırlıq və aqrotexniki tədbirlərin vaxtında və keyfiyyətlə aparılmasının məhsuldarlığa təsiri barəsində maarifləndirici tədbirlər davam etdirilmişdir. Kənd təsərr&uuml;fatında əl əməyinin y&uuml;ng&uuml;lləşdirilməsi və &ccedil;əkilən xərcin azaldılması &uuml;&ccedil;&uuml;n kənd təsərr&uuml;fatı texnikaları və aqreqatlarının g&uuml;c&uuml;ndən səmərəli istifadə edilməsi t&ouml;vsiyə olunmuşdur.Akademik Həsən Əliyev adına &ldquo;Araz&rdquo; Elm İstehsalat Birliyində Nax&ccedil;ıvan D&ouml;vlət Universitetinin Torpaqş&uuml;naslıq və aqrokimya ixtisasının tələbələrinə praktik dərslər ke&ccedil;ilmişdir. Birliyin Aqrokimya torpaqş&uuml;naslıq və torpaqların k&uuml;tləvi analizləriş&ouml;bəsinin əməkdaşları tərəfindən Kəngərli rayonunun B&ouml;y&uuml;kd&uuml;z, Xıncab və Təzəkənd kəndlərinin 44 min 53,8 hektar kənd təsərr&uuml;fatına yararlı sahələrindən 332 torpaq n&uuml;munələri g&ouml;t&uuml;r&uuml;lm&uuml;ş, tərkibindəki qida maddələrinin miqdarını m&uuml;əyyənləşdirmək &uuml;&ccedil;&uuml;n kimyəvi analizlər aparılmış, analizlərin nəticələrinə uyğun olaraq qida maddələri ilə təmin olunma kartoqramları Nax&ccedil;ıvan Muxtar Respublikası D&ouml;vlət Yerquruluşu, Geodeziya və Xəritə&ccedil;əkmə Layihə İnstitutunun əməkdaşları tərəfindən hazırlanaraq m&uuml;vafiq kənd icra n&uuml;mayəndəliklərinə verilmişdir. Heyvandarlıqda mal-qaranın cins tərkibinin yaxşılaşdırılması,s&uuml;ni mayalandırma tədbirlərinin davam etdirilməsi və heyvanların zoobaytar qaydalara uyğun yemləndirilməsi istiqamətində maarifləndirmə işləri davam etdirilmiş, arı&ccedil;ıların yaz aylarında aparacaqları tədbirlər onların diqqətinə &ccedil;atdırılmışdır.</span></p>\r\n\r\n<p style=\"text-align:right\">&nbsp;</p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı</strong></span></p>\r\n\r\n<p style=\"text-align:right\"><span style=\"font-size:16px\"><strong>Nazirliyinin mətbuat xidməti</strong></span></p>', 1, 18, '2021-11-29 03:25:14', '2021-11-29 04:01:21'),
(11, 'Aqrar sahədə maarifləndirmə işləri aparılır.', '<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nax&ccedil;ıvan Muxtar Respublikası Kənd Təsərr&uuml;fatı Nazirliyi yanvar ayında &ouml;z fəaliyyətini nazirliyin illik iş planı və d&ouml;vlət proqramlarının icrası istiqamətində qurmuş, maarifləndirici tədbirlər diqqətdə saxlanılmışdır. Belə ki, &ouml;tən ay istixanalarda şitillə əkilən tərəvəz sahələrində y&uuml;ksək məhsuldarlığa nail olunması &uuml;&ccedil;&uuml;n aqrotexniki tədbirlərin vaxtında və keyfiyyətlə aparılması barədə m&uuml;şavirələr ke&ccedil;irilmişdir. Meyvə və &uuml;z&uuml;m bağlarında, taxıl sahələrində gəmiricilərə qarşı m&uuml;şahidələr aparılaraq sahələr m&uuml;əyyənləşdirilmiş, onlara qarşı kimyəvi m&uuml;barizə aparılması qaydaları izah edilmişdir. Qoyun&ccedil;uluq və quş&ccedil;uluq təsərr&uuml;fatlarında heyvan və quşlara zoobaytar qaydalara uyğun qulluq edilməsi, yumurta istehsalının artırılması barədə də maarifləndirici tədbirlər təşkil olunmuşdur. &ldquo;Araz&rdquo; Elm-İstehsalat Birliyinin elmi şurasında kənd təsərr&uuml;fatında tədqiqat işlərinin aparılması m&ouml;vzuları m&uuml;zakirə edilmiş və təkliflər verilmişdir</span></p>', 1, 18, '2021-11-29 03:25:31', '2021-12-06 03:41:44'),
(21, 'Aqrar sığorta haqqında', '<p>Burada aqrar sığorta haqqında məlumat yerləşdiriləcək.</p>', 1, 28, '2022-01-31 07:25:45', '2022-02-01 06:07:46');

